                console.log('HTML audio blocked, trying Web Audio API');

                try {
                  // Use Web Audio API for better control
                  const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
                  const arrayBuffer = await audioBlob.arrayBuffer();
                  const audioBuffer = await audioContext.decodeAudioData(arrayBuffer);

                  const source = audioContext.createBufferSource();
                  source.buffer = audioBuffer;
                  source.connect(audioContext.destination);
                  source.start(0);

                  console.log('Web Audio API playback started');
                  setAudioEnabled(true);
                } catch (webAudioError) {
                  console.log('Both audio methods blocked - using fallback TTS');

                  // Fallback to browser TTS if ElevenLabs is blocked
                  try {
                    const utterance = new SpeechSynthesisUtterance(botResponse);
                    utterance.rate = 0.9;
                    utterance.pitch = 1.0;
                    utterance.volume = 0.8;
                    speechSynthesis.speak(utterance);
                    console.log('Browser TTS fallback used');
                    setAudioEnabled(true);
                  } catch (fallbackError) {
                    console.log('All audio methods failed');
                  }
                }
              }
            }, 100);
          } else {
            console.log('Empty audio blob received');
          }
        } else {
          console.log('Audio API request failed:', audioResponse.status);
          const errorText = await audioResponse.text();
          console.log('Error response:', errorText);
        }
        console.log('=== CHAT AUDIO DEBUG END ===');
      } catch (voiceError) {
        console.log('Voice generation error:', voiceError);
      }

      setBotStats(prev => prev ? {
        ...prev,
        wordsLearned: res.data.wordsLearned || prev.wordsLearned,
        stage: res.data.stage || prev.stage,
        level: res.data.stage === 'Infant' ? 1 : res.data.stage === 'Toddler' ? 2 : res.data.stage === 'Child' ? 3 : res.data.stage === 'Adolescent' ? 4 : 5
      } : null);

    } catch (err) {
      console.error('Chat failed', err);
      setMessages(prev => [...prev, {
        sender: 'bot',
        text: 'Sorry, I encountered an error. Please try again.',
        time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
      }]);
    }
    setLoading(false);
  };

