      try {
        const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
        const oscillator = audioContext.createOscillator();
        const gainNode = audioContext.createGain();

        oscillator.connect(gainNode);
        gainNode.connect(audioContext.destination);

        oscillator.frequency.value = 440;
        gainNode.gain.value = 0.1;

        oscillator.start();
        setTimeout(() => oscillator.stop(), 200);
        console.log('Browser beep test completed');
      } catch (beepError: any) {
        console.error('Browser beep failed:', beepError);
      }

      // Test ElevenLabs API
      const response = await fetch('/api/text-to-speech', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          text: 'Audio test successful',
          voiceId: selectedReflectionVoice
        })
      });

      if (response.ok) {
        const audioBlob = await response.blob();
        console.log('Test audio blob size:', audioBlob.size);

        if (audioBlob.size > 0) {
          const audioUrl = URL.createObjectURL(audioBlob);
          const audio = new Audio(audioUrl);

          audio.volume = 1.0;
          audio.muted = false;

          audio.play().then(() => {
            console.log('ElevenLabs TTS test successful');
            setAudioEnabled(true);
          }).catch(error => {
            console.log('Audio playback blocked:', error);
            setPendingAudio(audioUrl);
