  try {
    const { userId, agentType, objective } = req.body;

    if (!userId || !agentType || !objective) {
      return res.status(400).json({ error: 'Missing required fields' });
    }

    const session = await agentSystem.startAgentSession(
      parseInt(userId), 
      agentType, 
      objective
    );

    res.json({ 
