    'monthly-mindfulness': {
      type: 'theme',
      name: 'Zen Garden Theme',
      description: 'Peaceful green and earth tones with bamboo accents',
      preview: ' Soft greens, gentle animations, nature sounds',
      rarity: 'epic'
