import { getCurrentUserId } from "../utils/userSession";
import React, { useState } from 'react';
import { Trophy, Gift, Loader2, AlertCircle } from 'lucide-react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import axios, { AxiosResponse } from 'axios';

interface WellnessPointsBalance {
  userId: number;
  totalPoints: number;
  lifetimePoints: number;
  pointsSpent: number;
  currentLevel: number;
  pointsToNextLevel: number;
  levelProgress: number;
}

interface TherapeuticReward {
  id: number;
  name: string;
  description: string;
  category: string;
  pointsCost: number;
  rarity: string;
  therapeuticValue: string;
  isAvailable: boolean;
  canAfford?: boolean;
  isUnlocked?: boolean;
}

interface CommunityChallenge {
  id: number;
  name: string;
  description: string;
  challengeType: string;
  duration: number;
  startDate: string;
  endDate: string;
  targetGoal: number;
  pointsReward: number;
  participantCount: number;
  therapeuticFocus: string;
  dailyPrompts: any[];
  isParticipating?: boolean;
  progress?: number;
  completedDays?: number;
  daysRemaining?: number;
}

interface EmotionalAchievement {
  id: number;
  achievementId: string;
  name: string;
  description: string;
  category: string;
  badgeIcon: string;
  badgeColor: string;
  pointsReward: number;
  rarity: string;
  therapeuticSignificance: string;
  unlockedAt?: string;
}

interface UserReward {
  id: number;
  rewardId: number;
  name: string;
  category: string;
  pointsSpent: number;
  purchasedAt: string;
  isActive: boolean;
  isEquipped: boolean;
}

interface ApiResponse<T> {
  data: T;
  success: boolean;
  message?: string;
}

// Reusable Loading Component
const LoadingSpinner: React.FC<{ message?: string }> = ({ message = "Loading..." }) => (
  <div className="flex items-center justify-center py-8">
    <Loader2 className="w-6 h-6 animate-spin mr-2 text-blue-500" />
    <span className="text-sm text-gray-600">{message}</span>
  </div>
);

// Reusable Error Component
const ErrorMessage: React.FC<{ error: string; onRetry?: () => void }> = ({ error, onRetry }) => (
  <div className="flex flex-col items-center justify-center py-8 px-4 bg-red-50 border border-red-200 rounded-lg">
    <AlertCircle className="w-8 h-8 text-red-500 mb-2" />
    <h3 className="text-sm font-medium text-red-800 mb-1">Error Loading Data</h3>
    <p className="text-xs text-red-600 text-center mb-3">{error}</p>
    {onRetry && (
      <button
        onClick={onRetry}
        className="px-3 py-1 bg-red-100 hover:bg-red-200 text-red-700 text-xs rounded transition-colors"
      >
        Try Again
      </button>
    )}
  </div>
);

// Utility function to validate progress values
const validateProgress = (value: number | undefined): number => {
  if (typeof value !== 'number' || isNaN(value)) return 0;
  return Math.min(100, Math.max(0, value));
};

// Reusable Reward Card Component
const RewardCard: React.FC<{
  reward: TherapeuticReward;
  isProcessing: boolean;
  onPurchase: () => void;
}> = ({ reward, isProcessing, onPurchase }) => (
  <div className="bg-white border border-gray-200 rounded-lg p-4 hover:shadow-lg transition-shadow">
    <div className="flex justify-between items-start mb-3">
      <h3 className="font-semibold text-gray-900">{reward.name}</h3>
      <span className={`px-2 py-1 text-xs rounded ${
        reward.rarity === 'legendary' ? 'bg-yellow-100 text-yellow-800' :
        reward.rarity === 'epic' ? 'bg-purple-100 text-purple-800' :
        reward.rarity === 'rare' ? 'bg-blue-100 text-blue-800' :
        'bg-gray-100 text-gray-800'
      }`}>
        {reward.rarity}
      </span>
    </div>

    <p className="text-sm text-gray-600 mb-3">{reward.description}</p>

    <div className="flex justify-between items-center">
      <div className="flex items-center space-x-2">
        <span className="text-lg font-bold text-blue-600">{reward.pointsCost}</span>
        <span className="text-sm text-gray-500">points</span>
      </div>

      <button
        onClick={onPurchase}
        disabled={!reward.canAfford || !reward.isUnlocked || isProcessing}
        className={`px-3 py-1 text-sm rounded transition-colors ${
          reward.canAfford && reward.isUnlocked && !isProcessing
            ? 'bg-blue-600 text-white hover:bg-blue-700'
            : 'bg-gray-300 text-gray-500 cursor-not-allowed'
        }`}
      >
        {isProcessing ? (
          <div className="flex items-center space-x-1">
            <Loader2 className="w-3 h-3 animate-spin" />
            <span>Purchasing...</span>
          </div>
        ) : (
          'Purchase'
        )}
      </button>
    </div>
  </div>
);

// Reusable Challenge Card Component
const ChallengeCard: React.FC<{
  challenge: CommunityChallenge;
  isProcessing: boolean;
  onJoin: () => void;
}> = ({ challenge, isProcessing, onJoin }) => (
  <div className="bg-white border border-gray-200 rounded-lg p-4 hover:shadow-lg transition-shadow">
    <div className="flex justify-between items-start mb-3">
      <h3 className="font-semibold text-gray-900">{challenge.name}</h3>
      <span className="text-xs bg-blue-100 text-blue-800 px-2 py-1 rounded">
        {challenge.participantCount} participants
      </span>
    </div>

    <p className="text-sm text-gray-600 mb-3">{challenge.description}</p>

    <div className="mb-3">
      <div className="flex justify-between text-sm text-gray-500 mb-1">
        <span>Progress</span>
        <span>{validateProgress(challenge.progress)}%</span>
      </div>
      <div className="w-full bg-gray-200 rounded-full h-2">
        <div 
          className="bg-gradient-to-r from-blue-500 to-green-500 h-2 rounded-full transition-all"
          style={{ width: `${validateProgress(challenge.progress)}%` }}
        />
      </div>
    </div>

    <div className="flex justify-between items-center">
      <div className="text-sm text-gray-500">
        <span>{challenge.daysRemaining} days left</span>
        <span className="mx-2"></span>
        <span className="text-blue-600">+{challenge.pointsReward} points</span>
      </div>

      {!challenge.isParticipating ? (
        <button
          onClick={onJoin}
          disabled={isProcessing}
          className={`px-3 py-1 text-sm rounded transition-colors ${
            !isProcessing
              ? 'bg-green-600 text-white hover:bg-green-700'
              : 'bg-gray-300 text-gray-500 cursor-not-allowed'
          }`}
        >
          {isProcessing ? (
            <div className="flex items-center space-x-1">
              <Loader2 className="w-3 h-3 animate-spin" />
              <span>Joining...</span>
            </div>
          ) : (
            'Join Challenge'
          )}
        </button>
      ) : (
        <span className="px-3 py-1 text-sm bg-green-100 text-green-800 rounded">
          Participating
        </span>
      )}
    </div>
  </div>
);

const EnhancedGamificationDashboard: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'overview' | 'rewards' | 'challenges' | 'achievements'>('overview');
  // Removed unused selectedChallenge and selectedReward state variables
  const [processingRewards, setProcessingRewards] = useState<Set<number>>(new Set());
  const [processingChallenges, setProcessingChallenges] = useState<Set<number>>(new Set());

  const userId = getCurrentUserId();
  const queryClient = useQueryClient();

  // User validation
  if (!userId) {
    return (
      <div className="p-4">
        <ErrorMessage 
          error="User not found. Please log in or refresh the page." 
          onRetry={() => window.location.reload()}
        />
      </div>
    );
  }

  // Fetch gamification dashboard data
  const { 
    data: dashboardData, 
    isLoading: dashboardLoading, 
    error: dashboardError
  } = useQuery({
    queryKey: ['gamification', 'dashboard', userId],
    queryFn: async (): Promise<ApiResponse<{
      pointsBalance: WellnessPointsBalance;
      achievements: EmotionalAchievement[];
      userRewards: UserReward[];
      weeklyProgress: any;
    }>> => {
      const response: AxiosResponse<ApiResponse<any>> = await axios.get(
        `/api/gamification/dashboard/${userId}`
      );
      return response.data;
    },
    enabled: !!userId && activeTab === 'overview'
  });

  // Fetch rewards shop
  const { 
    data: rewardsData, 
    isLoading: rewardsLoading, 
    error: rewardsError,
    refetch: refetchRewards 
  } = useQuery({
    queryKey: ['gamification', 'rewards-shop', userId],
    queryFn: async (): Promise<ApiResponse<TherapeuticReward[]>> => {
      const response: AxiosResponse<ApiResponse<TherapeuticReward[]>> = await axios.get(
        `/api/rewards-shop/${userId}`
      );
      return response.data;
    },
    enabled: !!userId && activeTab === 'rewards'
  });

  // Fetch community challenges
  const { 
    data: challengesData, 
    isLoading: challengesLoading, 
    error: challengesError,
    refetch: refetchChallenges 
  } = useQuery({
    queryKey: ['gamification', 'community-challenges'],
    queryFn: async (): Promise<ApiResponse<CommunityChallenge[]>> => {
      const response: AxiosResponse<ApiResponse<CommunityChallenge[]>> = await axios.get(
        '/api/community-challenges'
      );
      return response.data;
    },
    enabled: !!userId && activeTab === 'challenges'
  });

  // Purchase reward mutation with individual item tracking
  const purchaseRewardMutation = useMutation({
    mutationFn: async (rewardId: number) => {
      setProcessingRewards(prev => new Set(prev).add(rewardId));
      const response = await axios.post(`/api/rewards-shop/${userId}/purchase`, { rewardId });
      return response.data;
    },
    onSuccess: (data, rewardId) => {
      queryClient.invalidateQueries({ queryKey: ['gamification', 'dashboard', userId] });
      queryClient.invalidateQueries({ queryKey: ['gamification', 'rewards-shop', userId] });
      setProcessingRewards(prev => {
        const newSet = new Set(prev);
        newSet.delete(rewardId);
        return newSet;
      });
    },
    onError: (error, rewardId) => {
      console.error('Failed to purchase reward:', error);
      setProcessingRewards(prev => {
        const newSet = new Set(prev);
        newSet.delete(rewardId);
        return newSet;
      });
    }
  });

  // Join challenge mutation with individual item tracking
  const joinChallengeMutation = useMutation({
    mutationFn: async (challengeId: number) => {
      setProcessingChallenges(prev => new Set(prev).add(challengeId));
      const response = await axios.post(`/api/community-challenges/${challengeId}/join`, { userId });
      return response.data;
    },
    onSuccess: (data, challengeId) => {
      queryClient.invalidateQueries({ queryKey: ['gamification', 'dashboard', userId] });
      queryClient.invalidateQueries({ queryKey: ['gamification', 'community-challenges'] });
      setProcessingChallenges(prev => {
        const newSet = new Set(prev);
        newSet.delete(challengeId);
        return newSet;
      });
    },
    onError: (error, challengeId) => {
      console.error('Failed to join challenge:', error);
      setProcessingChallenges(prev => {
        const newSet = new Set(prev);
        newSet.delete(challengeId);
        return newSet;
      });
    }
  });

  // Safe data extraction with proper fallbacks
  const wellnessPoints: WellnessPointsBalance = dashboardData?.data?.pointsBalance || {
    userId,
    totalPoints: 0,
    lifetimePoints: 0,
    pointsSpent: 0,
    currentLevel: 1,
    pointsToNextLevel: 100,
    levelProgress: 0
  };

  const rewards: TherapeuticReward[] = rewardsData?.data || [];
  const challenges: CommunityChallenge[] = challengesData?.data || [];
  const userAchievements: EmotionalAchievement[] = dashboardData?.data?.achievements || [];
  const userRewards: UserReward[] = dashboardData?.data?.userRewards || [];

  // Enhanced handlers with validation and error feedback
  const handlePurchaseReward = (reward: TherapeuticReward) => {
    if (!reward.canAfford) {
      console.warn('Insufficient points to purchase this reward');
      return;
    }
    if (!reward.isUnlocked) {
      console.warn('This reward is not yet unlocked');
      return;
    }
    if (processingRewards.has(reward.id)) {
      return; // Already processing
    }

    purchaseRewardMutation.mutate(reward.id);
  };

  const handleJoinChallenge = (challenge: CommunityChallenge) => {
    if (challenge.isParticipating) {
      console.warn('Already participating in this challenge');
      return;
    }
    if (processingChallenges.has(challenge.id)) {
      return; // Already processing
    }

    joinChallengeMutation.mutate(challenge.id);
  };

  const getRarityColor = (rarity: string) => {
    switch (rarity) {
      case 'legendary': return 'text-yellow-600';
      case 'epic': return 'text-purple-600';
      case 'rare': return 'text-blue-600';
      default: return 'text-gray-600';
    }
  };

  // Type-safe icon mapping for achievement categories
  const categoryIconMap = new Map([
    ['resilience', ''],
    ['emotional_breakthrough', ''], 
    ['self_awareness', ''],
    ['mindfulness', ''],
    ['social_connection', ''],
    ['coping_skills', ''],
    ['progress_milestone', '']
  ]);

  const getCategoryIcon = (category: string): string => {
    return categoryIconMap.get(category) || '';
  };

  // Optimized badge lookup using Map for O(1) performance
  const achievementMap = new Map(userAchievements.map(achievement => [achievement.id, achievement]));

  // Enhanced rarity color mapping with better contrast
  const rarityColorMap = new Map([
    ['legendary', 'text-yellow-700 bg-yellow-50 border-yellow-200'],
    ['epic', 'text-purple-700 bg-purple-50 border-purple-200'],
    ['rare', 'text-blue-700 bg-blue-50 border-blue-200'],
    ['common', 'text-gray-700 bg-gray-50 border-gray-200']
  ]);

  // Show loading states based on active tab
  if (activeTab === 'overview' && dashboardLoading) {
    return <LoadingSpinner message="Loading wellness dashboard..." />;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#E6E6FA] via-white to-[#ADD8E6] p-4">
      <div className="max-w-7xl mx-auto">
        {/* Header with Wellness Points */}
        <div className="bg-white/80 backdrop-blur-sm rounded-3xl shadow-xl p-6 mb-6">
          <div className="flex flex-col md:flex-row justify-between items-center">
            <div>
              <h1 className="text-3xl font-bold text-gray-800 mb-2">Wellness Journey</h1>
              <p className="text-gray-600">Your path to mental wellness through positive reinforcement</p>
            </div>

            <div className="mt-4 md:mt-0 flex flex-col items-center">
              <div className="bg-gradient-to-r from-[#ADD8E6] to-[#98FB98] text-white rounded-2xl px-6 py-4 text-center">
                <div className="text-2xl font-bold">{wellnessPoints.totalPoints}</div>
                <div className="text-sm opacity-90">Wellness Points</div>
              </div>

              <div className="mt-3 flex items-center space-x-4">
                <div className="text-center">
                  <div className="text-lg font-semibold text-gray-700">{wellnessPoints.currentLevel}</div>
                  <div className="text-xs text-gray-500">Level</div>
                </div>

                <div className="w-32 bg-gray-200 rounded-full h-2">
                  <div 
                    className="bg-gradient-to-r from-[#ADD8E6] to-[#98FB98] h-2 rounded-full transition-all duration-300"
                    style={{ width: `${validateProgress(wellnessPoints.levelProgress * 100)}%` }}
                  ></div>
                </div>

                <div className="text-center">
                  <div className="text-lg font-semibold text-gray-700">{wellnessPoints.pointsToNextLevel}</div>
                  <div className="text-xs text-gray-500">To Next Level</div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Navigation Tabs */}
        <div className="w-full bg-white rounded-lg p-1 mb-6 shadow-lg">
          <div className="grid grid-cols-4 gap-1">
            {[
              { id: 'overview', label: 'Overview', icon: '' },
              { id: 'rewards', label: 'Rewards Shop', icon: '' },
              { id: 'challenges', label: 'Challenges', icon: '' },
              { id: 'achievements', label: 'Achievements', icon: '' }
            ].map((tab) => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id as any)}
                className={`w-full px-4 py-4 text-sm font-bold rounded-md transition-all border-2 text-white ${
                  activeTab === tab.id
                    ? 'bg-blue-600 border-blue-400 shadow-lg'
                    : 'bg-gray-700 border-gray-600 hover:bg-gray-800'
                }`}
              >
                <span className="text-lg text-white">{tab.icon}</span>
                <div className="text-sm mt-1 text-white">{tab.label}</div>
              </button>
            ))}
          </div>
        </div>

        {/* Overview Tab */}
        {activeTab === 'overview' && (
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Recent Achievements */}
            <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg p-6">
              <h3 className="text-xl font-bold text-gray-800 mb-4 flex items-center">
                <span className="mr-2"></span>
                Recent Achievements
              </h3>

              <div className="space-y-3">
                {userAchievements.slice(0, 3).map((achievement) => (
                  <div key={`achievement-${achievement.id}`} className="flex items-center space-x-3 p-3 bg-gradient-to-r from-gray-50 to-white rounded-xl">
                    <div className="text-2xl">{achievement.badgeIcon}</div>
                    <div className="flex-1">
                      <h4 className="font-semibold text-gray-800">{achievement.name}</h4>
                      <p className="text-sm text-gray-600">{achievement.description}</p>
                      <div className="flex items-center mt-1">
                        <span className={`text-xs px-2 py-1 rounded-full ${getRarityColor(achievement.rarity)} bg-gray-100`}>
                          {achievement.rarity}
                        </span>
                        <span className="text-xs text-gray-500 ml-2">+{achievement.pointsReward} points</span>
                      </div>
                    </div>
                  </div>
                ))}

                {userAchievements.length === 0 && (
                  <div className="text-center py-8 text-gray-500">
                    <div className="text-4xl mb-2"></div>
                    <p>Keep engaging to unlock your first achievement!</p>
                  </div>
                )}
              </div>
            </div>

            {/* Active Challenges */}
            <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg p-6">
              <h3 className="text-xl font-bold text-gray-800 mb-4 flex items-center">
                <span className="mr-2"></span>
                Active Challenges
              </h3>

              <div className="space-y-3">
                {challenges.filter(c => c.isParticipating).slice(0, 3).map((challenge) => (
                  <div key={`challenge-${challenge.id}`} className="p-3 bg-gradient-to-r from-gray-50 to-white rounded-xl">
                    <div className="flex justify-between items-start mb-2">
                      <h4 className="font-semibold text-gray-800">{challenge.name}</h4>
                      <span className="text-xs bg-green-100 text-green-600 px-2 py-1 rounded-full">
                        {challenge.completedDays || 0}/{challenge.targetGoal}
                      </span>
                    </div>
                    <p className="text-sm text-gray-600 mb-2">{challenge.description}</p>
                    <div className="w-full bg-gray-200 rounded-full h-2">
                      <div 
                        className="bg-gradient-to-r from-[#ADD8E6] to-[#98FB98] h-2 rounded-full"
                        style={{ width: `${validateProgress(challenge.progress)}%` }}
                      ></div>
                    </div>
                    <div className="flex justify-between items-center mt-2">
                      <span className="text-xs text-gray-500">{challenge.daysRemaining} days left</span>
                      <span className="text-xs text-blue-600">+{challenge.pointsReward} points</span>
                    </div>
                  </div>
                ))}

                {challenges.filter(c => c.isParticipating).length === 0 && (
                  <div className="text-center py-8 text-gray-500">
                    <div className="text-4xl mb-2"></div>
                    <p>Join a challenge to start your wellness journey!</p>
                  </div>
                )}
              </div>
            </div>

            {/* Recent Rewards */}
            <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg p-6">
              <h3 className="text-xl font-bold text-gray-800 mb-4 flex items-center">
                <span className="mr-2"></span>
                Your Rewards
              </h3>

              <div className="space-y-3">
                {userRewards.slice(0, 3).map((reward) => (
                  <div key={`user-reward-${reward.id}`} className="flex items-center space-x-3 p-3 bg-gradient-to-r from-gray-50 to-white rounded-xl">
                    <div className="w-12 h-12 bg-gradient-to-r from-[#ADD8E6] to-[#98FB98] rounded-xl flex items-center justify-center">
                      <span className="text-white font-bold">
                        {reward.category === 'avatar' ? '' : 
                         reward.category === 'theme' ? '' : 
                         reward.category === 'premium_content' ? '' : ''}
                      </span>
                    </div>
                    <div className="flex-1">
                      <h4 className="font-semibold text-gray-800">{reward.name}</h4>
                      <p className="text-sm text-gray-600">{reward.pointsSpent} points spent</p>
                      {reward.isEquipped && (
                        <span className="text-xs bg-green-100 text-green-600 px-2 py-1 rounded-full">Equipped</span>
                      )}
                    </div>
                  </div>
                ))}

                {userRewards.length === 0 && (
                  <div className="text-center py-8 text-gray-500">
                    <div className="text-4xl mb-2"></div>
                    <p>Visit the rewards shop to spend your wellness points!</p>
                  </div>
                )}
              </div>
            </div>

            {/* Stats Overview */}
            <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg p-6">
              <h3 className="text-xl font-bold text-gray-800 mb-4 flex items-center">
                <span className="mr-2"></span>
                Wellness Stats
              </h3>

              <div className="grid grid-cols-2 gap-4">
                <div className="text-center p-4 bg-gradient-to-r from-blue-50 to-blue-100 rounded-xl">
                  <div className="text-2xl font-bold text-blue-600">{wellnessPoints.lifetimePoints}</div>
                  <div className="text-sm text-blue-800">Lifetime Points</div>
                </div>

                <div className="text-center p-4 bg-gradient-to-r from-green-50 to-green-100 rounded-xl">
                  <div className="text-2xl font-bold text-green-600">{userAchievements.length || 0}</div>
                  <div className="text-sm text-green-800">Achievements</div>
                </div>

                <div className="text-center p-4 bg-gradient-to-r from-purple-50 to-purple-100 rounded-xl">
                  <div className="text-2xl font-bold text-purple-600">{challenges.filter(c => c.isParticipating).length || 0}</div>
                  <div className="text-sm text-purple-800">Active Challenges</div>
                </div>

                <div className="text-center p-4 bg-gradient-to-r from-pink-50 to-pink-100 rounded-xl">
                  <div className="text-2xl font-bold text-pink-600">{wellnessPoints.pointsSpent}</div>
                  <div className="text-sm text-pink-800">Points Spent</div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Rewards Shop Tab */}
        {activeTab === 'rewards' && (
          <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg p-6">
            <h3 className="text-2xl font-bold text-gray-800 mb-6 flex items-center">
              <span className="mr-3"></span>
              Therapeutic Rewards Shop
            </h3>

            {rewardsError && (
              <ErrorMessage 
                error={rewardsError.message || 'Failed to load rewards shop'} 
                onRetry={() => refetchRewards()}
              />
            )}

            {rewardsLoading ? (
              <LoadingSpinner message="Loading rewards shop..." />
            ) : rewards.length === 0 ? (
              <div className="text-center py-12">
                <Gift className="w-16 h-16 mx-auto text-gray-400 mb-4" />
                <h3 className="text-lg font-medium text-gray-600 mb-2">No Rewards Available</h3>
                <p className="text-gray-500">Check back later for new rewards!</p>
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {rewards.map((reward) => (
                  <RewardCard
                    key={`reward-${reward.id}`}
                    reward={reward}
                    isProcessing={processingRewards.has(reward.id)}
                    onPurchase={() => handlePurchaseReward(reward)}
                  />
                ))}
              </div>
            )}
          </div>
        )}

        {/* Community Challenges Tab */}
        {activeTab === 'challenges' && (
          <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg p-6">
            <h3 className="text-2xl font-bold text-gray-800 mb-6 flex items-center">
              <span className="mr-3"></span>
              Community Wellness Challenges
            </h3>

            {challengesError && (
              <ErrorMessage 
                error={challengesError.message || 'Failed to load challenges'} 
                onRetry={() => refetchChallenges()}
              />
            )}

            {challengesLoading ? (
              <LoadingSpinner message="Loading challenges..." />
            ) : challenges.length === 0 ? (
              <div className="text-center py-12">
                <Trophy className="w-16 h-16 mx-auto text-gray-400 mb-4" />
                <h3 className="text-lg font-medium text-gray-600 mb-2">No Challenges Available</h3>
                <p className="text-gray-500">Check back later for new challenges!</p>
              </div>
            ) : (
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {challenges.map((challenge) => (
                  <ChallengeCard
                    key={`challenge-${challenge.id}`}
                    challenge={challenge}
                    isProcessing={processingChallenges.has(challenge.id)}
                    onJoin={() => handleJoinChallenge(challenge)}
                  />
                ))}
              </div>
            )}
          </div>
        )}

        {/* Emotional Achievements Tab */}
        {activeTab === 'achievements' && (
          <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg p-6">
            <h3 className="text-2xl font-bold text-gray-800 mb-6 flex items-center">
              <span className="mr-3"></span>
              Emotional Achievements
            </h3>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {userAchievements.map((achievement, index) => (
                <div 
                  key={index}
                  className="p-4 rounded-2xl bg-gradient-to-br from-yellow-50 to-white border-2 border-yellow-200"
                >
                  <div className="flex items-center space-x-3 mb-3">
                    <div 
                      className="w-12 h-12 rounded-full flex items-center justify-center text-2xl"
                      style={{ backgroundColor: achievement.badgeColor + '20', color: achievement.badgeColor }}
                    >
                      {achievement.badgeIcon}
                    </div>
                    <div>
                      <h4 className="font-bold text-gray-800">{achievement.name}</h4>
                      <span className={`text-sm px-2 py-1 rounded-full ${getRarityColor(achievement.rarity)} bg-gray-100`}>
                        {achievement.rarity}
                      </span>
                    </div>
                  </div>

                  <p className="text-sm text-gray-600 mb-3">{achievement.description}</p>

                  <div className="bg-blue-50 rounded-lg p-3 mb-3">
                    <h5 className="text-xs font-semibold text-blue-800 mb-1">Therapeutic Significance:</h5>
                    <p className="text-xs text-blue-700">{achievement.therapeuticSignificance}</p>
                  </div>

                  <div className="flex justify-between items-center text-sm">
                    <span className="text-gray-500">
                      {getCategoryIcon(achievement.category)} {achievement.category.replace('_', ' ')}
                    </span>
                    <span className="font-semibold text-green-600">+{achievement.pointsReward} points</span>
                  </div>

                  {achievement.unlockedAt && (
                    <div className="mt-2 text-xs text-gray-500">
                      Unlocked {new Date(achievement.unlockedAt).toLocaleDateString()}
                    </div>
                  )}
                </div>
              ))}

              {userAchievements.length === 0 && (
                <div className="col-span-full text-center py-12 text-gray-500">
                  <div className="text-6xl mb-4"></div>
                  <h4 className="text-xl font-semibold mb-2">No Achievements Yet</h4>
                  <p>Continue your therapeutic journey to unlock meaningful achievements that recognize your emotional growth and resilience.</p>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default EnhancedGamificationDashboard;

client/src/components/FeedbackSystem.tsx.content:
import React, { useState, useCallback } from 'react';
import { MessageSquare, Bug, Lightbulb, Send, CheckCircle, AlertCircle, Star, Loader2, RefreshCw } from 'lucide-react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import axios, { AxiosResponse } from 'axios';
import { getCurrentUserId } from '../utils/userSession';

// Types and Interfaces
interface FeedbackItem {
  id: number;
  userId: number;
  type: 'bug' | 'feature' | 'general';
  title: string;
  description: string;
  priority: 'low' | 'medium' | 'high';
  status: 'submitted' | 'reviewed' | 'in_progress' | 'resolved';
  rating?: number;
  createdAt: string;
}

interface FeedbackSubmission {
  userId: number;
  type: 'bug' | 'feature' | 'general';
  title: string;
  description: string;
  priority: 'low' | 'medium' | 'high';
  rating?: number;
}

interface ApiResponse<T> {
  success: boolean;
  data?: T;
  error?: string;
  feedback?: T[];
}

type FeedbackFormErrors = {
  title?: string;
  description?: string;
  general?: string;
};

// Utility Components
const LoadingSpinner: React.FC<{ message?: string }> = ({ message }) => (
  <div className="flex flex-col items-center justify-center py-12">
    <Loader2 className="w-8 h-8 animate-spin text-[var(--theme-accent)] mb-4" />
    <p className="theme-text-secondary">{message || 'Loading...'}</p>
  </div>
);

const ErrorMessage: React.FC<{ error: string; onRetry?: () => void }> = ({ error, onRetry }) => (
  <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-6">
    <div className="flex items-center justify-between">
      <div className="flex items-center">
        <AlertCircle className="w-5 h-5 text-red-600 mr-3" />
        <div>
          <h3 className="text-red-800 font-medium">Error</h3>
          <p className="text-red-700 text-sm mt-1">{error}</p>
        </div>
      </div>
      {onRetry && (
        <button
          onClick={onRetry}
          className="flex items-center px-3 py-1 bg-red-100 hover:bg-red-200 text-red-700 rounded-md text-sm font-medium transition-colors"
        >
          <RefreshCw className="w-4 h-4 mr-1" />
          Retry
        </button>
      )}
    </div>
  </div>
);

const SuccessAlert: React.FC<{ message: string }> = ({ message }) => (
  <div className="mb-6 p-4 bg-green-50 border border-green-200 rounded-lg flex items-center">
    <CheckCircle className="text-green-600 mr-3 flex-shrink-0" size={20} />
    <span className="text-green-800">{message}</span>
  </div>
);

const EmptyState: React.FC<{ 
  icon: React.ReactNode; 
  title: string; 
  description: string;
  action?: React.ReactNode;
}> = ({ icon, title, description, action }) => (
  <div className="text-center py-12">
    <div className="mb-4">{icon}</div>
    <h3 className="text-lg font-medium theme-text mb-2">{title}</h3>
    <p className="theme-text-secondary mb-4">{description}</p>
    {action}
  </div>
);

// Color mapping objects for consistent styling
const typeColors = {
  bug: 'text-red-500',
  feature: 'text-yellow-500',
  general: 'text-blue-500'
} as const;

const statusColors = {
  submitted: 'text-gray-500',
  reviewed: 'text-blue-400',
  in_progress: 'text-yellow-400',
  resolved: 'text-green-400'
} as const;

const priorityColors = {
  high: 'text-red-400 bg-red-400/20',
  medium: 'text-yellow-400 bg-yellow-400/20',
  low: 'text-green-400 bg-green-400/20'
} as const;

const feedbackTypes = [
  { value: 'general' as const, label: 'General Feedback', icon: MessageSquare, colorClass: typeColors.general },
  { value: 'bug' as const, label: 'Bug Report', icon: Bug, colorClass: typeColors.bug },
  { value: 'feature' as const, label: 'Feature Request', icon: Lightbulb, colorClass: typeColors.feature }
];

// Main Component
const FeedbackSystem: React.FC = () => {
  const [feedbackType, setFeedbackType] = useState<'bug' | 'feature' | 'general'>('general');
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [priority, setPriority] = useState<'low' | 'medium' | 'high'>('medium');
  const [rating, setRating] = useState<number>(0);
  const [activeTab, setActiveTab] = useState<'submit' | 'history'>('submit');
  const [formErrors, setFormErrors] = useState<FeedbackFormErrors>({});
  const [showSuccess, setShowSuccess] = useState(false);

  const userId = getCurrentUserId();
  const queryClient = useQueryClient();

  // Validation functions
  const validateForm = useCallback((): FeedbackFormErrors => {
    const errors: FeedbackFormErrors = {};

    if (!title.trim()) {
      errors.title = 'Title is required';
    } else if (title.trim().length < 3) {
      errors.title = 'Title must be at least 3 characters';
    }

    if (!description.trim()) {
      errors.description = 'Description is required';
    } else if (description.trim().length < 10) {
      errors.description = 'Description must be at least 10 characters';
    }

    return errors;
  }, [title, description]);

  // API functions with proper error handling
  const fetchFeedbackHistory = useCallback(async (): Promise<FeedbackItem[]> => {
    if (!userId) throw new Error('User authentication required');

    const response: AxiosResponse<ApiResponse<FeedbackItem>> = await axios.get(`/api/feedback/${userId}`);

    if (!response.data.success) {
      throw new Error(response.data.error || 'Failed to load feedback history');
    }

    return response.data.feedback || [];
  }, [userId]);

  const submitFeedback = useCallback(async (feedbackData: FeedbackSubmission): Promise<void> => {
    const response: AxiosResponse<ApiResponse<void>> = await axios.post('/api/feedback', feedbackData);

    if (!response.data.success) {
      throw new Error(response.data.error || 'Failed to submit feedback');
    }
  }, []);

  // React Query hooks
  const {
    data: userFeedback = [],
    isLoading: feedbackLoading,
    error: feedbackError,
    refetch: refetchFeedback
  } = useQuery({
    queryKey: ['/api/feedback', userId],
    queryFn: fetchFeedbackHistory,
    enabled: !!userId && activeTab === 'history',
    retry: 2,
    staleTime: 5 * 60 * 1000 // 5 minutes
  });

  const submitFeedbackMutation = useMutation({
    mutationFn: submitFeedback,
    onSuccess: () => {
      // Reset form
      setTitle('');
      setDescription('');
      setRating(0);
      setPriority('medium');
      setFormErrors({});
      setShowSuccess(true);

      // Invalidate and refetch feedback history
      queryClient.invalidateQueries({ queryKey: ['/api/feedback', userId] });

      // Hide success message after 5 seconds
      setTimeout(() => setShowSuccess(false), 5000);
    },
    onError: (error: Error) => {
      setFormErrors({ general: error.message });
    }
  });

  // Event handlers
  const handleSubmit = useCallback((e: React.FormEvent) => {
    e.preventDefault();

    const errors = validateForm();
    setFormErrors(errors);

    if (Object.keys(errors).length > 0) {
      return;
    }

    if (!userId) {
      setFormErrors({ general: 'User authentication required' });
      return;
    }

    const feedbackData: FeedbackSubmission = {
      userId,
      type: feedbackType,
      title: title.trim(),
      description: description.trim(),
      priority,
      ...(feedbackType === 'general' && rating > 0 && { rating })
    };

    submitFeedbackMutation.mutate(feedbackData);
  }, [feedbackType, title, description, priority, rating, userId, validateForm, submitFeedbackMutation]);

  const handleTabChange = useCallback((tab: 'submit' | 'history') => {
    setActiveTab(tab);
    setFormErrors({});
  }, []);

  // Helper functions
  const getTypeIcon = useCallback((type: string) => {
    const typeConfig = feedbackTypes.find(t => t.value === type);
    if (!typeConfig) return <MessageSquare className={typeColors.general} size={20} />;

    const IconComponent = typeConfig.icon;
    return <IconComponent className={typeConfig.colorClass} size={20} />;
  }, []);

  const getStatusColor = useCallback((status: string) => {
    return statusColors[status as keyof typeof statusColors] || statusColors.submitted;
  }, []);

  const getPriorityColor = useCallback((priority: string) => {
    return priorityColors[priority as keyof typeof priorityColors] || 'theme-text-secondary theme-surface';
  }, []);

  // User validation
  if (!userId) {
    return (
      <div className="min-h-screen theme-background p-4">
        <div className="max-w-4xl mx-auto">
          <ErrorMessage 
            error="Please log in to access the feedback system" 
          />
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen theme-background p-4">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center mb-4">
            <MessageSquare className="mr-3 theme-text" size={32} />
            <h1 className="text-3xl font-bold theme-text font-serif">Feedback & Suggestions</h1>
          </div>
          <p className="theme-text-secondary text-lg leading-relaxed max-w-2xl mx-auto">
            Help us improve Chakrai by sharing your thoughts, reporting bugs, or suggesting new features. 
            Your feedback is invaluable in making this a better wellness companion for everyone.
          </p>
        </div>

        {/* Tab Navigation */}
        <div className="flex justify-center mb-8">
          <div className="flex theme-surface rounded-lg p-1 border border-[var(--theme-accent)]/30">
            <button
              onClick={() => handleTabChange('submit')}
              className={`px-6 py-2 rounded-md font-medium transition-all ${
                activeTab === 'submit'
                  ? 'bg-[var(--theme-accent)] text-white shadow-sm'
                  : 'theme-text-secondary hover:theme-text'
              }`}
              aria-label="Submit new feedback"
            >
              Submit Feedback
            </button>
            <button
              onClick={() => handleTabChange('history')}
              className={`px-6 py-2 rounded-md font-medium transition-all ${
                activeTab === 'history'
                  ? 'bg-[var(--theme-accent)] text-white shadow-sm'
                  : 'theme-text-secondary hover:theme-text'
              }`}
              aria-label={`View feedback history (${userFeedback.length} items)`}
            >
              My Feedback ({userFeedback.length})
            </button>
          </div>
        </div>

        {/* Submit Tab */}
        {activeTab === 'submit' && (
          <div className="max-w-2xl mx-auto">
            {showSuccess && (
              <SuccessAlert message="Thank you! Your feedback has been submitted successfully." />
            )}

            {formErrors.general && (
              <ErrorMessage error={formErrors.general} />
            )}

            <div className="theme-card rounded-xl p-8 border border-[var(--theme-accent)]/30 shadow-lg">
              <form onSubmit={handleSubmit} className="space-y-6" noValidate>
                {/* Feedback Type Selection */}
                <div>
                  <label className="block theme-text text-sm font-medium mb-3">
                    Feedback Type
                  </label>
                  <div className="grid grid-cols-3 gap-3">
                    {feedbackTypes.map(({ value, label, icon: Icon, colorClass }) => (
                      <button
                        key={value}
                        type="button"
                        onClick={() => setFeedbackType(value)}
                        className={`p-4 rounded-lg border-2 transition-all text-center theme-surface hover-lift ${
                          feedbackType === value
                            ? 'border-[var(--theme-accent)] bg-[var(--theme-accent)]/20'
                            : 'border-[var(--theme-accent)]/30 hover:border-[var(--theme-accent)]/50'
                        }`}
                        aria-label={`Select ${label}`}
                        aria-pressed={feedbackType === value}
                      >
                        <Icon className={`mx-auto mb-2 ${colorClass}`} size={24} />
                        <div className="text-sm font-medium theme-text">{label}</div>
                      </button>
                    ))}
                  </div>
                </div>

                {/* Title */}
                <div>
                  <label htmlFor="feedback-title" className="block theme-text text-sm font-medium mb-2">
                    {feedbackType === 'bug' ? 'Bug Summary' : 
                     feedbackType === 'feature' ? 'Feature Title' : 'Feedback Title'}
                    <span className="text-red-500 ml-1" aria-label="required">*</span>
                  </label>
                  <input
                    id="feedback-title"
                    type="text"
                    value={title}
                    onChange={(e) => {
                      setTitle(e.target.value);
                      if (formErrors.title) {
                        setFormErrors(prev => ({ ...prev, title: undefined }));
                      }
                    }}
                    placeholder={
                      feedbackType === 'bug' ? 'Brief description of the bug...' :
                      feedbackType === 'feature' ? 'What feature would you like to see?' :
                      'What would you like to share?'
                    }
                    className={`w-full p-3 rounded-lg border-2 theme-surface theme-text transition-colors ${
                      formErrors.title 
                        ? 'border-red-400 focus:border-red-500' 
                        : 'border-[var(--theme-accent)]/30 focus:border-[var(--theme-accent)]'
                    }`}
                    aria-describedby={formErrors.title ? 'title-error' : undefined}
                    aria-invalid={!!formErrors.title}
                    required
                  />
                  {formErrors.title && (
                    <p id="title-error" className="text-red-600 text-sm mt-1" role="alert">
                      {formErrors.title}
                    </p>
                  )}
                </div>

                {/* Description */}
                <div>
                  <label htmlFor="feedback-description" className="block theme-text text-sm font-medium mb-2">
                    {feedbackType === 'bug' ? 'Steps to Reproduce / Details' : 'Description'}
                    <span className="text-red-500 ml-1" aria-label="required">*</span>
                  </label>
                  <textarea
                    id="feedback-description"
                    value={description}
                    onChange={(e) => {
                      setDescription(e.target.value);
                      if (formErrors.description) {
                        setFormErrors(prev => ({ ...prev, description: undefined }));
                      }
                    }}
                    placeholder={
                      feedbackType === 'bug' ? 'Please describe the bug in detail, including steps to reproduce it...' :
                      feedbackType === 'feature' ? 'Describe the feature and how it would help you...' :
                      'Share your thoughts, suggestions, or feedback...'
                    }
                    rows={6}
                    className={`w-full p-3 rounded-lg border-2 theme-surface theme-text resize-none transition-colors ${
                      formErrors.description 
                        ? 'border-red-400 focus:border-red-500' 
                        : 'border-[var(--theme-accent)]/30 focus:border-[var(--theme-accent)]'
                    }`}
                    aria-describedby={formErrors.description ? 'description-error' : undefined}
                    aria-invalid={!!formErrors.description}
                    required
                  />
                  {formErrors.description && (
                    <p id="description-error" className="text-red-600 text-sm mt-1" role="alert">
                      {formErrors.description}
                    </p>
                  )}
                </div>

                {/* Priority (for bugs and features) */}
                {(feedbackType === 'bug' || feedbackType === 'feature') && (
                  <div>
                    <label htmlFor="feedback-priority" className="block theme-text text-sm font-medium mb-2">
                      Priority
                    </label>
                    <select
                      id="feedback-priority"
                      value={priority}
                      onChange={(e) => setPriority(e.target.value as 'low' | 'medium' | 'high')}
                      className="w-full p-3 rounded-lg border-2 border-[var(--theme-accent)]/30 focus:border-[var(--theme-accent)] theme-surface theme-text"
                    >
                      <option value="low">Low - Minor issue or nice-to-have</option>
                      <option value="medium">Medium - Noticeable issue or useful feature</option>
                      <option value="high">High - Major issue or important feature</option>
                    </select>
                  </div>
                )}

                {/* Rating (for general feedback) */}
                {feedbackType === 'general' && (
                  <div>
                    <label className="block theme-text text-sm font-medium mb-2">
                      Overall Rating (Optional)
                    </label>
                    <div className="flex space-x-1" role="radiogroup" aria-label="Rating">
                      {[1, 2, 3, 4, 5].map((star) => (
                        <button
                          key={star}
                          type="button"
                          onClick={() => setRating(star)}
                          className="p-1 rounded focus:outline-none focus:ring-2 focus:ring-[var(--theme-accent)]"
                          aria-label={`Rate ${star} star${star !== 1 ? 's' : ''}`}
                          role="radio"
                          aria-checked={star === rating}
                        >
                          <Star
                            className={`w-8 h-8 transition-colors ${
                              star <= rating ? 'text-[var(--theme-accent)] fill-[var(--theme-accent)]' : 'theme-text-secondary'
                            }`}
                          />
                        </button>
                      ))}
                    </div>
                  </div>
                )}

                {/* Submit Button */}
                <button
                  type="submit"
                  disabled={submitFeedbackMutation.isPending}
                  className="w-full bg-gradient-to-r from-[var(--theme-primary)] to-[var(--theme-accent)] text-white py-3 px-6 rounded-lg font-medium transition-all duration-300 hover:shadow-lg disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center"
                  aria-label="Submit feedback form"
                >
                  {submitFeedbackMutation.isPending ? (
                    <>
                      <Loader2 className="w-5 h-5 animate-spin mr-2" />
                      Submitting...
                    </>
                  ) : (
                    <>
                      <Send className="mr-2" size={18} />
                      Submit Feedback
                    </>
                  )}
                </button>
              </form>
            </div>
          </div>
        )}

        {/* History Tab */}
        {activeTab === 'history' && (
          <div className="max-w-4xl mx-auto">
            {feedbackError && (
              <ErrorMessage 
                error={feedbackError.message || 'Failed to load feedback history'} 
                onRetry={() => refetchFeedback()}
              />
            )}

            {feedbackLoading ? (
              <LoadingSpinner message="Loading your feedback history..." />
            ) : userFeedback.length === 0 ? (
              <EmptyState
                icon={<MessageSquare className="mx-auto theme-text-secondary" size={48} />}
                title="No feedback submitted yet"
                description="Click 'Submit Feedback' to share your thoughts!"
                action={
                  <button
                    onClick={() => handleTabChange('submit')}
                    className="bg-[var(--theme-accent)] text-white px-6 py-2 rounded-lg font-medium hover:shadow-lg transition-all"
                  >
                    Submit Your First Feedback
                  </button>
                }
              />
            ) : (
              <div className="space-y-4">
                {userFeedback.map((feedback) => (
                  <div 
                    key={`feedback-${feedback.id}`} 
                    className="theme-card rounded-lg p-6 border border-[var(--theme-accent)]/30"
                  >
                    <div className="flex items-start justify-between mb-4">
                      <div className="flex items-center">
                        {getTypeIcon(feedback.type)}
                        <h3 className="theme-text font-semibold ml-2">{feedback.title}</h3>
                      </div>
                      <div className="flex items-center space-x-2">
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${getPriorityColor(feedback.priority)}`}>
                          {feedback.priority.toUpperCase()}
                        </span>
                        <span className={`text-sm font-medium ${getStatusColor(feedback.status)}`}>
                          {feedback.status.replace('_', ' ').toUpperCase()}
                        </span>
                      </div>
                    </div>

                    <p className="theme-text-secondary mb-3">{feedback.description}</p>

                    <div className="flex items-center justify-between text-sm theme-text-secondary">
                      <span>Submitted: {new Date(feedback.createdAt).toLocaleDateString()}</span>
                      {feedback.rating && (
                        <div className="flex items-center" aria-label={`Rating: ${feedback.rating} stars`}>
                          <span className="mr-1">Rating:</span>
                          {[...Array(feedback.rating)].map((_, i) => (
                            <Star 
                              key={i} 
                              className="w-4 h-4 text-[var(--theme-accent)] fill-[var(--theme-accent)]" 
                              aria-hidden="true"
                            />
                          ))}
                        </div>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default FeedbackSystem;

