import React, { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import axios from 'axios';
import { getCurrentUserId } from '../utils/userSession';
import { 
  Activity, 
  Heart, 
  TrendingUp, 
  Zap, 
  Shield, 
  Smartphone, 
  CheckCircle, 
  Clock, 
  WifiOff, 
  AlertCircle, 
  Wifi 
} from 'lucide-react';

// Types
interface WearableDevice {
  id: number;
  userId: number;
  deviceType: string;
  deviceName: string;
  syncStatus: string;
  lastSyncTime: string;
  consentGranted: boolean;
  privacyLevel: string;
}

interface HealthMetric {
  id: number;
  userId: number;
  metricType: string;
  metricValue: string;
  unit: string;
  recordedAt: string;
  dataQuality: string;
  contextTags: string[];
}

interface HealthCorrelation {
  id: number;
  userId: number;
  healthMetric: string;
  emotionalState: string;
  correlationStrength: string;
  confidenceScore: string;
  timeframe: string;
  insights: string;
}

interface HealthInsight {
