import React, { useState } from 'react';
import { ChevronRight, ChevronLeft, CheckCircle, Brain, Heart, MessageCircle, Target } from 'lucide-react';

interface PersonalityQuizProps {
  onComplete: (profile: UserProfile) => void;
  onSkip?: () => void;
}

interface QuizQuestion {
  id: number;
  question: string;
  category: 'communication' | 'emotional' | 'goals' | 'support';
  options: {
    value: string;
    label: string;
    weight: number;
  }[];
}

interface UserProfile {
  communicationStyle: 'direct' | 'gentle' | 'encouraging' | 'analytical';
  emotionalSupport: 'high' | 'moderate' | 'minimal';
  preferredTone: 'casual' | 'professional' | 'warm' | 'straightforward';
  primaryGoals: string[];
  stressResponses: string[];
  motivationFactors: string[];
  sessionPreference: 'short' | 'medium' | 'long';
  personalityTraits: string[];
}

const quizQuestions: QuizQuestion[] = [
  {
    id: 1,
    category: 'communication',
    question: "How do you prefer to receive feedback or guidance?",
    options: [
      { value: 'direct', label: 'Direct and straightforward', weight: 1 },
      { value: 'gentle', label: 'Gentle and encouraging', weight: 2 },
      { value: 'detailed', label: 'Detailed explanations with examples', weight: 3 },
      { value: 'supportive', label: 'Warm and emotionally supportive', weight: 4 }
    ]
  },
  {
    id: 2,
    category: 'emotional',
    question: "When you're feeling overwhelmed, what helps you most?",
    options: [
      { value: 'practical', label: 'Practical steps to solve the problem', weight: 1 },
      { value: 'validation', label: 'Someone to listen and validate my feelings', weight: 2 },
      { value: 'distraction', label: 'Activities to distract and reset my mind', weight: 3 },
      { value: 'reflection', label: 'Quiet time for self-reflection', weight: 4 }
    ]
  },
  {
    id: 3,
    category: 'goals',
    question: "What's your primary wellness goal?",
    options: [
      { value: 'stress', label: 'Managing stress and anxiety', weight: 1 },
      { value: 'mood', label: 'Improving overall mood and happiness', weight: 2 },
      { value: 'relationships', label: 'Better relationships and communication', weight: 3 },
      { value: 'growth', label: 'Personal growth and self-understanding', weight: 4 }
    ]
  },
  {
    id: 4,
    category: 'communication',
    question: "How do you like to process difficult emotions?",
    options: [
      { value: 'talking', label: 'Talking through them with someone', weight: 1 },
      { value: 'writing', label: 'Writing or journaling about them', weight: 2 },
      { value: 'thinking', label: 'Thinking them through quietly', weight: 3 },
      { value: 'activity', label: 'Working through them with activities', weight: 4 }
    ]
  },
  {
    id: 5,
    category: 'support',
    question: "What type of encouragement motivates you most?",
    options: [
      { value: 'achievement', label: 'Recognition of progress and achievements', weight: 1 },
      { value: 'potential', label: 'Reminders of your strengths and potential', weight: 2 },
      { value: 'understanding', label: 'Feeling truly understood and heard', weight: 3 },
      { value: 'challenge', label: 'Gentle challenges to grow and improve', weight: 4 }
    ]
  },
  {
    id: 6,
    category: 'goals',
    question: "How do you prefer to set and track goals?",
    options: [
      { value: 'specific', label: 'Specific, measurable targets with deadlines', weight: 1 },
      { value: 'flexible', label: 'Flexible intentions that adapt to life', weight: 2 },
      { value: 'small', label: 'Small daily habits that build over time', weight: 3 },
      { value: 'intuitive', label: 'Following what feels right in the moment', weight: 4 }
    ]
  },
  {
    id: 7,
    category: 'emotional',
    question: "When facing a challenge, you typically:",
    options: [
      { value: 'analyze', label: 'Analyze the situation logically', weight: 1 },
      { value: 'feel', label: 'Process the emotions it brings up', weight: 2 },
      { value: 'act', label: 'Take action to address it immediately', weight: 3 },
      { value: 'seek', label: 'Seek advice from others', weight: 4 }
    ]
  },
  {
    id: 8,
    category: 'support',
    question: "How much emotional support do you typically need?",
    options: [
      { value: 'minimal', label: 'I prefer to handle things independently', weight: 1 },
      { value: 'occasional', label: 'I like check-ins when things get tough', weight: 2 },
      { value: 'regular', label: 'I appreciate regular emotional support', weight: 3 },
      { value: 'frequent', label: 'I thrive with frequent encouragement', weight: 4 }
    ]
  },
  {
    id: 9,
    category: 'communication',
    question: "What session length works best for you?",
    options: [
      { value: 'short', label: 'Quick check-ins (5-10 minutes)', weight: 1 },
      { value: 'medium', label: 'Moderate conversations (15-20 minutes)', weight: 2 },
      { value: 'long', label: 'Deep discussions (30+ minutes)', weight: 3 },
      { value: 'variable', label: 'It depends on what I need that day', weight: 4 }
    ]
  },
  {
    id: 10,
    category: 'goals',
    question: "What would success in your wellness journey look like?",
    options: [
      { value: 'peace', label: 'Feeling more at peace and centered', weight: 1 },
      { value: 'confident', label: 'Being more confident and self-assured', weight: 2 },
      { value: 'resilient', label: 'Handling life\'s ups and downs better', weight: 3 },
      { value: 'fulfilled', label: 'Living a more authentic, fulfilled life', weight: 4 }
    ]
  }
];

const categoryIcons = {
  communication: MessageCircle,
  emotional: Heart,
  goals: Target,
  support: Brain
};

export default function PersonalityQuiz({ onComplete, onSkip }: PersonalityQuizProps) {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState<Record<number, string>>({});
  const [isComplete, setIsComplete] = useState(false);

  const handleAnswer = (questionId: number, value: string) => {
    setAnswers(prev => ({
      ...prev,
      [questionId]: value
    }));
  };

  const handleNext = () => {
    if (currentQuestion < quizQuestions.length - 1) {
      setCurrentQuestion(prev => prev + 1);
    } else {
      completeQuiz();
    }
  };

  const handlePrevious = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion(prev => prev - 1);
    }
  };

  const completeQuiz = () => {
    const profile = analyzeAnswers(answers);
    setIsComplete(true);
    setTimeout(() => {
      onComplete(profile);
    }, 2000);
  };

  const analyzeAnswers = (answers: Record<number, string>): UserProfile => {
    // Analyze communication style
    const commAnswers = [answers[1], answers[4], answers[9]];
    const communicationStyle = determineCommunicationStyle(commAnswers);

    // Analyze emotional support needs
    const emotionalAnswers = [answers[2], answers[7], answers[8]];
    const emotionalSupport = determineEmotionalSupport(emotionalAnswers);

    // Analyze preferred tone
    const preferredTone = determinePreferredTone([answers[1], answers[5]]);

    // Extract goals and traits
    const primaryGoals = extractGoals([answers[3], answers[6], answers[10]]);
    const stressResponses = [answers[2]];
    const motivationFactors = [answers[5]];
    const sessionPreference = determineSessionPreference(answers[9]);
    const personalityTraits = extractPersonalityTraits(answers);

    return {
      communicationStyle,
      emotionalSupport,
      preferredTone,
      primaryGoals,
      stressResponses,
      motivationFactors,
      sessionPreference,
      personalityTraits
    };
  };

  const determineCommunicationStyle = (answers: string[]): 'direct' | 'gentle' | 'encouraging' | 'analytical' => {
    if (answers.includes('direct') || answers.includes('analyze')) return 'direct';
    if (answers.includes('gentle') || answers.includes('supportive')) return 'gentle';
    if (answers.includes('detailed') || answers.includes('thinking')) return 'analytical';
    return 'encouraging';
  };

  const determineEmotionalSupport = (answers: string[]): 'high' | 'moderate' | 'minimal' => {
    if (answers.includes('frequent') || answers.includes('regular')) return 'high';
    if (answers.includes('minimal')) return 'minimal';
    return 'moderate';
  };

  const determinePreferredTone = (answers: string[]): 'casual' | 'professional' | 'warm' | 'straightforward' => {
    if (answers.includes('supportive') || answers.includes('understanding')) return 'warm';
    if (answers.includes('direct') || answers.includes('achievement')) return 'straightforward';
    if (answers.includes('detailed') || answers.includes('analyze')) return 'professional';
    return 'casual';
  };

  const extractGoals = (answers: string[]): string[] => {
    const goalMap: Record<string, string> = {
      'stress': 'Stress Management',
      'mood': 'Mood Improvement',
      'relationships': 'Better Relationships',
      'growth': 'Personal Growth',
      'specific': 'Goal Achievement',
      'flexible': 'Adaptive Wellness',
      'small': 'Habit Building',
      'peace': 'Inner Peace',
      'confident': 'Self-Confidence',
      'resilient': 'Emotional Resilience',
      'fulfilled': 'Authentic Living'
    };

    return answers.map(answer => goalMap[answer]).filter(Boolean);
  };

  const determineSessionPreference = (answer: string): 'short' | 'medium' | 'long' => {
    if (answer === 'short') return 'short';
    if (answer === 'long') return 'long';
    return 'medium';
  };

  const extractPersonalityTraits = (answers: Record<number, string>): string[] => {
    const traits: string[] = [];

    if (answers[7] === 'analyze') traits.push('Analytical');
    if (answers[7] === 'feel') traits.push('Emotionally Aware');
    if (answers[4] === 'writing') traits.push('Reflective');
    if (answers[4] === 'talking') traits.push('Verbal Processor');
    if (answers[2] === 'practical') traits.push('Solution-Oriented');
    if (answers[8] === 'minimal') traits.push('Independent');
    if (answers[8] === 'frequent') traits.push('Community-Oriented');

    return traits;
  };

  const currentQ = quizQuestions[currentQuestion];
  const IconComponent = categoryIcons[currentQ.category];
  const progress = ((currentQuestion + 1) / quizQuestions.length) * 100;
  const selectedAnswer = answers[currentQ.id];

  if (isComplete) {
    return (
      <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50 p-4">
        <div className="bg-gradient-to-br from-blue-900 to-purple-900 rounded-3xl p-8 max-w-md w-full text-center border border-blue-400/30">
          <div className="animate-bounce mb-6">
            <CheckCircle className="w-16 h-16 text-green-400 mx-auto" />
          </div>
          <h3 className="text-2xl font-bold text-white mb-4">Profile Complete!</h3>
          <p className="text-blue-200">
            Creating your personalized wellness companion experience...
          </p>
          <div className="mt-6">
            <div className="animate-spin w-8 h-8 border-2 border-blue-400 border-t-transparent rounded-full mx-auto"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50 p-4">
      <div className="bg-gradient-to-br from-blue-900 to-purple-900 rounded-3xl p-8 max-w-2xl w-full border border-blue-400/30">
        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center space-x-3">
              <IconComponent className="w-6 h-6 text-blue-300" />
              <h2 className="text-2xl font-bold text-white">Personality Assessment</h2>
            </div>
            <span className="text-blue-300 text-sm font-medium">
              {currentQuestion + 1} of {quizQuestions.length}
            </span>
          </div>

          {/* Progress Bar */}
          <div className="w-full bg-blue-800/30 rounded-full h-2">
            <div 
              className="bg-gradient-to-r from-blue-400 to-purple-400 h-2 rounded-full transition-all duration-300"
              style={{ width: `${progress}%` }}
            ></div>
          </div>
        </div>

        {/* Question */}
        <div className="mb-8">
          <h3 className="text-xl font-semibold text-white mb-6 leading-relaxed">
            {currentQ.question}
          </h3>

          <div className="space-y-4">
            {currentQ.options.map((option) => (
              <button
                key={option.value}
                onClick={() => handleAnswer(currentQ.id, option.value)}
                className={`w-full p-4 rounded-xl text-left transition-all duration-200 border-2 ${
                  selectedAnswer === option.value
                    ? 'bg-blue-600/50 border-blue-400 text-white shadow-lg'
                    : 'bg-blue-800/20 border-blue-600/30 text-blue-100 hover:bg-blue-700/30 hover:border-blue-500/50'
                }`}
              >
                <div className="flex items-center justify-between">
                  <span className="font-medium">{option.label}</span>
                  {selectedAnswer === option.value && (
                    <CheckCircle className="w-5 h-5 text-blue-300" />
                  )}
                </div>
              </button>
            ))}
          </div>
        </div>

        {/* Navigation */}
        <div className="flex items-center justify-between">
          <button
            onClick={handlePrevious}
            disabled={currentQuestion === 0}
            className="flex items-center space-x-2 px-6 py-3 bg-blue-700/50 hover:bg-blue-600/50 disabled:bg-blue-800/30 disabled:text-blue-400 text-white rounded-xl transition-colors disabled:cursor-not-allowed"
          >
            <ChevronLeft size={20} />
            <span>Previous</span>
          </button>

          <div className="flex items-center space-x-4">
            {onSkip && currentQuestion === 0 && (
              <button
                onClick={onSkip}
                className="px-6 py-3 text-blue-300 hover:text-white transition-colors"
              >
                Skip for now
              </button>
            )}

            <button
              onClick={handleNext}
              disabled={!selectedAnswer}
              className="flex items-center space-x-2 px-6 py-3 bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-500 hover:to-purple-500 disabled:from-blue-800 disabled:to-purple-800 disabled:text-blue-400 text-white rounded-xl transition-all disabled:cursor-not-allowed"
            >
              <span>{currentQuestion === quizQuestions.length - 1 ? 'Complete' : 'Next'}</span>
              <ChevronRight size={20} />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/PersonalityReflect.content:
import React, { useState, useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import { RefreshCw, Brain, TrendingUp, User, RotateCcw } from 'lucide-react';
import { getCurrentUserId } from '../utils/userSession';

interface PersonalityReflectionData {
  communicationStyle: string;
  emotionalPatterns: string[];
  strengths: string[];
  growthOpportunities: string[];
  personalityInsights: {
    dominantTraits: string[];
    communicationPreference: string;
    emotionalProcessing: string;
  };
  wellnessRecommendations: string[];
  dataPoints: {
    journalEntries: number;
    conversationMessages: number;
    moodDataPoints: number;
  };
  analysisStatus?: string;
  lastUpdated?: string;
}

interface PersonalityReflectionProps {
  userId?: number;
}

const PersonalityReflection: React.FC<PersonalityReflectionProps> = ({ userId }) => {
  // Get current user ID from session context
  const currentUserId = userId || getCurrentUserId();
  const [refreshTrigger, setRefreshTrigger] = useState(0);

  const { data, isLoading, error, refetch } = useQuery({
    queryKey: ['personality-reflection', refreshTrigger],
    queryFn: async (): Promise<PersonalityReflectionData> => {
      const deviceFingerprint = localStorage.getItem('deviceFingerprint') || 
                               `device_${Math.random().toString(36).substring(2, 15)}`;
      const sessionId = localStorage.getItem('sessionId') || 
                       `session_${Math.random().toString(36).substring(2, 15)}`;

      localStorage.setItem('deviceFingerprint', deviceFingerprint);
      localStorage.setItem('sessionId', sessionId);

      const response = await fetch(`/api/analytics/personality-reflection/${currentUserId}`, {
        headers: {
          'X-Device-Fingerprint': deviceFingerprint,
          'X-Session-ID': sessionId
        }
      });

      if (!response.ok) {
        throw new Error('Failed to fetch personality reflection');
      }
      return response.json();
    },
    refetchInterval: 300000, // Refresh every 5 minutes
  });

  const handleRefresh = () => {
    setRefreshTrigger(prev => prev + 1);
    refetch();
  };

  if (isLoading) {
    return (
      <div className="p-6 h-full theme-primary">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-2xl font-bold text-white flex items-center gap-2">
            <Brain className="w-6 h-6 text-[#9fa8da]" />
            Personality Reflection
          </h2>
        </div>
        <div className="theme-primary/30 backdrop-blur-sm rounded-xl p-6 border border-[#9fa8da]/50">
          <div className="animate-pulse space-y-4">
            <div className="h-4 bg-white/30 rounded w-3/4"></div>
            <div className="h-4 bg-white/30 rounded w-1/2"></div>
            <div className="h-4 bg-white/30 rounded w-5/6"></div>
            <div className="h-4 bg-white/30 rounded w-2/3"></div>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-6 h-full theme-primary">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-2xl font-bold text-white flex items-center gap-2">
            <Brain className="w-6 h-6 text-[#9fa8da]" />
            Personality Reflection
          </h2>
          <button
            onClick={handleRefresh}
            className="p-2 theme-primary text-white rounded-lg hover:theme-primary transition-colors"
          >
            <RefreshCw className="w-4 h-4" />
          </button>
        </div>
        <div className="bg-red-900/30 border border-red-600/50 rounded-xl p-6">
          <p className="text-red-200">Unable to generate your personality reflection. Please try refreshing or continue engaging with the platform to build more data for analysis.</p>
        </div>
      </div>
    );
  }

  const formatReflectionText = (text: string) => {
    // If no structured format, just display as paragraphs
    if (!text.includes('1.') && !text.includes('TRAIT')) {
      return (
        <div className="space-y-4 text-white leading-relaxed">
          {text.split('\n').filter(line => line.trim()).map((paragraph, index) => (
            <p key={index} className="text-white/90 leading-relaxed">
              {paragraph.trim()}
            </p>
          ))}
        </div>
      );
    }

    // Split by numbered sections and format nicely
    const sections = text.split(/(?=\d+\.\s+[A-Z\s]+:)/);

    return sections.map((section, index) => {
      if (!section.trim()) return null;

      const lines = section.trim().split('\n');
      const title = lines[0] || '';
      const content = lines.slice(1).join(' ').trim(); // Join with spaces, not newlines

      // Check if this is a numbered section
      const isNumberedSection = /^\d+\.\s+[A-Z\s]+:/.test(title || '');

      if (isNumberedSection) {
        const cleanTitle = title.replace(/^\d+\.\s+/, '').replace(':', '').trim();
        return (
          <div key={index} className="mb-6">
            <h3 className="font-semibold text-[#9fa8da] mb-3 flex items-center gap-2">
              {cleanTitle.includes('TRAIT') && <User className="w-4 h-4" />}
              {cleanTitle.includes('POSITIVE') && <TrendingUp className="w-4 h-4" />}
              {cleanTitle.includes('GROWTH') && <RotateCcw className="w-4 h-4" />}
              {cleanTitle.includes('EMOTIONAL') && <Brain className="w-4 h-4" />}
              <span>{cleanTitle}</span>
            </h3>
            <p className="text-white/90 leading-relaxed">{content}</p>
          </div>
        );
      } else {
        return (
          <p key={index} className="text-white/90 leading-relaxed mb-4">
            {section.trim()}
          </p>
        );
      }
    }).filter(Boolean);
  };

  return (
    <div className="p-6 h-full theme-primary overflow-y-auto">
      <div className="flex items-center justify-between mb-4">
        <h2 className="text-2xl font-bold text-white flex items-center gap-2">
          <Brain className="w-6 h-6 text-[#9fa8da]" />
          Personality Reflection
        </h2>
        <button
          onClick={handleRefresh}
          className="p-2 theme-primary text-white rounded-lg hover:theme-primary transition-colors flex items-center gap-2"
          title="Refresh Analysis"
        >
          <RefreshCw className="w-4 h-4" />
          Refresh
        </button>
      </div>

      {/* Data Points Summary */}
      <div className="theme-primary/30 backdrop-blur-sm rounded-xl p-4 mb-4 border border-[#9fa8da]/50">
        <div className="text-sm text-white/80 mb-2">
          <span className="block mb-2">Analysis based on:</span>
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-2 text-xs">
            <div className="bg-white/10 rounded-lg px-3 py-2 text-center">
              <div className="font-semibold text-lg text-white">{data?.dataPoints.conversationMessages || 0}</div>
              <div className="text-white/70">conversations</div>
            </div>
            <div className="bg-white/10 rounded-lg px-3 py-2 text-center">
              <div className="font-semibold text-lg text-white">{data?.dataPoints.journalEntries || 0}</div>
              <div className="text-white/70">journal entries</div>
            </div>
            <div className="bg-white/10 rounded-lg px-3 py-2 text-center">
              <div className="font-semibold text-lg text-white">{data?.dataPoints.moodDataPoints || 0}</div>
              <div className="text-white/70">mood entries</div>
            </div>
          </div>
        </div>
        <div className="text-xs text-white/60 mt-2 text-center">
          Last updated: {data?.lastUpdated ? new Date(data.lastUpdated).toLocaleString() : 'Unknown'}
        </div>
      </div>

      {/* AI Personality Analysis */}
      <div className="theme-primary/30 backdrop-blur-sm rounded-xl p-6 space-y-4 border border-[#9fa8da]/50">
        <div className="flex items-center gap-2 mb-4">
          <Brain className="w-5 h-5 text-[#BBDEFB]" />
          <h3 className="text-lg font-semibold text-white">AI Personality Analysis</h3>
        </div>

        {data ? (
          <div className="space-y-6">
            {/* Communication Style */}
            <div>
              <h4 className="text-lg font-medium text-white mb-2">Communication Style</h4>
              <p className="text-white/90 leading-relaxed">{data.communicationStyle}</p>
            </div>

            {/* Emotional Patterns */}
            <div>
              <h4 className="text-lg font-medium text-white mb-2">Emotional Patterns</h4>
              <ul className="space-y-2">
                {data.emotionalPatterns?.map((pattern, index) => (
                  <li key={index} className="text-white/90 flex items-start gap-2">
                    <span className="text-[#9fa8da] mt-1"></span>
                    {pattern}
                  </li>
                ))}
              </ul>
            </div>

            {/* Strengths */}
            <div>
              <h4 className="text-lg font-medium text-white mb-2">Strengths</h4>
              <div className="flex flex-wrap gap-2">
                {data.strengths?.map((strength, index) => (
                  <span key={index} className="bg-green-600/20 text-green-200 px-3 py-1 rounded-full text-sm">
                    {strength}
                  </span>
                ))}
              </div>
            </div>

            {/* Growth Opportunities */}
            <div>
              <h4 className="text-lg font-medium text-white mb-2">Growth Opportunities</h4>
              <div className="flex flex-wrap gap-2">
                {data.growthOpportunities?.map((opportunity, index) => (
                  <span key={index} className="bg-blue-600/20 text-blue-200 px-3 py-1 rounded-full text-sm">
                    {opportunity}
                  </span>
                ))}
              </div>
            </div>

            {/* Wellness Recommendations */}
            <div>
              <h4 className="text-lg font-medium text-white mb-2">Wellness Recommendations</h4>
              <ul className="space-y-2">
                {data.wellnessRecommendations?.map((recommendation, index) => (
                  <li key={index} className="text-white/90 flex items-start gap-2">
                    <span className="text-[#9fa8da] mt-1"></span>
                    {recommendation}
                  </li>
                ))}
              </ul>
            </div>
          </div>
        ) : (
          <p className="text-white/70 italic">
            Continue engaging with Trai through conversations and journaling to build your personality profile.
          </p>
        )}
      </div>

      {/* Engagement Encouragement */}
      {(!data?.dataPoints.conversationMessages || data.dataPoints.conversationMessages < 3) && (
        <div className="bg-blue-50 border border-blue-200 rounded-xl p-4 mt-4">
          <p className="text-blue-700 text-sm">
            <strong>Tip:</strong> Have more conversations and write journal entries to get deeper personality insights and therapeutic guidance.
          </p>
        </div>
      )}
    </div>
  );
};

export default PersonalityReflection;

client/src/components/PremiumGate.tsx.content:
import React from 'react';
import { useSubscription } from '../contexts/SubscriptionContext';
import { Crown, Lock, Zap, Star } from 'lucide-react';

interface PremiumGateProps {
  feature: string;
  children: React.ReactNode;
  fallback?: React.ReactNode;
  showUpgrade?: boolean;
}

const PremiumGate: React.FC<PremiumGateProps> = ({ 
  feature, 
  children, 
  fallback, 
  showUpgrade = true 
}) => {
  const { isPremium, checkUsageLimit, upgradeSubscription } = useSubscription();
  const { canUse, remaining, limit } = checkUsageLimit(feature);

  if (isPremium || canUse) {
    return <>{children}</>;
  }

  if (fallback) {
    return <>{fallback}</>;
  }

  return (
    <div className="theme-card p-6 rounded-lg border border-[var(--theme-accent)]/30 text-center">
      <div className="flex justify-center mb-4">
        <div className="w-16 h-16 bg-gradient-to-br from-yellow-400 to-orange-500 rounded-full flex items-center justify-center">
          <Crown className="w-8 h-8 text-white" />
        </div>
      </div>

      <h3 className="text-lg font-semibold theme-text mb-2">Premium Feature</h3>
      <p className="theme-text-secondary text-sm mb-4">
        {remaining === 0 ? 
          `You've used all ${limit} free ${feature.replace('_', ' ')} for this month.` :
          `This feature requires a premium subscription.`
        }
      </p>

      {showUpgrade && (
        <button
          onClick={upgradeSubscription}
          className="bg-gradient-to-r from-yellow-400 to-orange-500 text-white px-6 py-3 rounded-lg font-semibold hover:from-yellow-500 hover:to-orange-600 transition-all duration-200 flex items-center space-x-2 mx-auto"
        >
          <Zap className="w-4 h-4" />
          <span>Upgrade to Premium</span>
        </button>
      )}
    </div>
  );
};

interface UsageBadgeProps {
  feature: string;
  compact?: boolean;
}

export const UsageBadge: React.FC<UsageBadgeProps> = ({ feature, compact = false }) => {
  const { isPremium, checkUsageLimit } = useSubscription();
  const { remaining, limit } = checkUsageLimit(feature);

  if (isPremium) {
    return (
      <div className={`inline-flex items-center space-x-1 px-2 py-1 bg-gradient-to-r from-yellow-400 to-orange-500 text-white text-xs rounded-full ${compact ? '' : 'mb-2'}`}>
        <Crown className="w-3 h-3" />
        <span>Premium</span>
      </div>
    );
  }

  if (remaining === 0) {
    return (
      <div className={`inline-flex items-center space-x-1 px-2 py-1 bg-red-500 text-white text-xs rounded-full ${compact ? '' : 'mb-2'}`}>
        <Lock className="w-3 h-3" />
        <span>Limit Reached</span>
      </div>
    );
  }

  return (
    <div className={`inline-flex items-center space-x-1 px-2 py-1 bg-blue-500 text-white text-xs rounded-full ${compact ? '' : 'mb-2'}`}>
      <span>{remaining}/{limit} remaining</span>
    </div>
  );
};

interface PremiumButtonProps {
  feature: string;
  onClick: () => void;
  children: React.ReactNode;
  className?: string;
  disabled?: boolean;
}

export const PremiumButton: React.FC<PremiumButtonProps> = ({ 
  feature, 
  onClick, 
  children, 
  className = '', 
  disabled = false 
}) => {
  const { isPremium, checkUsageLimit, upgradeSubscription } = useSubscription();
  const { canUse } = checkUsageLimit(feature);

  const handleClick = () => {
    if (isPremium || canUse) {
      onClick();
    } else {
      upgradeSubscription();
    }
  };

  const isDisabled = disabled || (!isPremium && !canUse);

  return (
    <button
      onClick={handleClick}
      disabled={isDisabled}
      className={`${className} ${isDisabled ? 'opacity-50 cursor-not-allowed' : ''} relative group`}
    >
      {children}
      {!isPremium && !canUse && (
        <div className="absolute -top-1 -right-1">
          <Crown className="w-4 h-4 text-yellow-500" />
        </div>
      )}
    </button>
  );
};

export default PremiumGate;

