                    <div className="flex justify-between items-center mt-2">
                      <div className="flex gap-1">
                        {recentEntry.tags?.slice(0, 3).map(tag => (
                          <span key={tag} className="bg-blue-500/30 text-blue-100 px-2 py-1 rounded text-xs">
