  forum_id: number;
  author_id: number | null;
  anonymous_name: string | null;
  title: string;
  content: string;
  is_anonymous: boolean;
  support_count: number;
  reply_count: number;
  is_flagged: boolean;
  created_at: string;
  updated_at: string;
}

