import helmet from 'helmet';
import rateLimit from 'express-rate-limit';
import { body, validationResult } from 'express-validator';
import { Request, Response, NextFunction } from 'express';

// Rate limiting configurations
export const authLimiter = rateLimit({
  windowMs: 15 * 60 * 1000, // 15 minutes
  max: 5, // 5 attempts per window
  message: {
    error: 'Too many authentication attempts, please try again later.'
  },
  standardHeaders: true,
  legacyHeaders: false,
  trustProxy: 1, // Trust first proxy only for security
});

export const generalLimiter = rateLimit({
  windowMs: 15 * 60 * 1000, // 15 minutes
  max: 100, // 100 requests per window
  message: {
    error: 'Too many requests, please try again later.'
  },
  standardHeaders: true,
  legacyHeaders: false,
  trustProxy: 1, // Trust first proxy only for security
});

export const uploadLimiter = rateLimit({
  windowMs: 60 * 60 * 1000, // 1 hour
  max: 20, // 20 uploads per hour
  message: {
    error: 'Too many file uploads, please try again later.'
  },
  standardHeaders: true,
  legacyHeaders: false,
  trustProxy: 1, // Trust first proxy only for security
});

// Helmet configuration for security headers - Development friendly
export const helmetConfig = helmet({
  contentSecurityPolicy: process.env.NODE_ENV === 'development' ? false : {
    directives: {
      defaultSrc: ["'self'"],
      styleSrc: ["'self'", "'unsafe-inline'", "https://fonts.googleapis.com"],
      fontSrc: ["'self'", "https://fonts.gstatic.com"],
      imgSrc: ["'self'", "data:", "https:"],
      scriptSrc: ["'self'", "'unsafe-inline'", "'unsafe-eval'"],
      connectSrc: ["'self'", "https://api.openai.com", "https://api.elevenlabs.io", "ws:", "wss:"],
      mediaSrc: ["'self'", "blob:"],
    },
  },
  crossOriginEmbedderPolicy: false, // For web audio processing
  crossOriginResourcePolicy: { policy: "cross-origin" },
});

// Input validation middleware
export const validateRegistration = [
  body('email')
    .isEmail()
    .normalizeEmail()
    .withMessage('Valid email is required'),
  body('password')
    .isLength({ min: 8, max: 128 })
    .matches(/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]/)
    .withMessage('Password must be 8-128 chars with uppercase, lowercase, number, and special character'),
  body('name')
    .trim()
    .isLength({ min: 1, max: 100 })
    .escape()
    .withMessage('Name is required and must be 1-100 characters'),
];

export const validateLogin = [
  body('email')
    .isEmail()
    .normalizeEmail()
    .withMessage('Valid email is required'),
  body('password')
    .isLength({ min: 1 })
    .withMessage('Password is required'),
];

export const validateJournalEntry = [
  body('content')
    .trim()
    .isLength({ min: 1, max: 10000 })
    .withMessage('Journal content must be 1-10000 characters'),
  body('mood')
    .optional()
    .isIn(['very_negative', 'negative', 'neutral', 'positive', 'very_positive'])
    .withMessage('Invalid mood value'),
  body('intensity')
    .optional()
    .isInt({ min: 1, max: 10 })
    .withMessage('Intensity must be 1-10'),
];

export const validateFileUpload = (req: Request, res: Response, next: NextFunction) => {
  if (!req.file) {
    return res.status(400).json({ error: 'No file uploaded' });
  }

  const allowedTypes = ['audio/wav', 'audio/mpeg', 'audio/mp3', 'audio/webm'];
  const maxSize = 10 * 1024 * 1024; // 10MB

  if (!allowedTypes.includes(req.file.mimetype)) {
    return res.status(400).json({ 
      error: 'Invalid file type. Only WAV, MP3, and WebM audio files are allowed.' 
    });
  }

  if (req.file.size > maxSize) {
    return res.status(400).json({ 
      error: 'File too large. Maximum size is 10MB.' 
    });
  }

  next();
};

// Validation result handler
export const handleValidationErrors = (req: Request, res: Response, next: NextFunction) => {
  const errors = validationResult(req);
  if (!errors.isEmpty()) {
    return res.status(400).json({
      error: 'Validation failed',
      details: errors.array()
    });
  }
  next();
};

// Secure CORS configuration
export const corsConfig = {
  origin: function (origin: string | undefined, callback: (err: Error | null, allow?: boolean) => void) {
    // Allow requests with no origin (mobile apps, curl, etc.)
    if (!origin) return callback(null, true);

    // In development, be more permissive
    if (process.env.NODE_ENV === 'development') {
      console.log('CORS check - Origin:', origin);
      // Allow all localhost and replit domains in development
      return callback(null, true);
    }

    // In production, only allow specific domains
    const allowedOrigins = process.env.ALLOWED_ORIGINS?.split(',') || [];
    if (allowedOrigins.includes(origin)) {
      return callback(null, true);
    }

    callback(new Error('Not allowed by CORS'), false);
  },
  methods: ['GET', 'POST', 'PUT', 'DELETE', 'PATCH', 'OPTIONS'],
  allowedHeaders: [
    'Origin', 
    'X-Requested-With', 
    'Content-Type', 
    'Accept', 
    'Authorization', 
    'X-Device-Fingerprint', 
    'X-Session-Id'
  ],
  credentials: true,
  maxAge: 86400 // 24 hours
};

// HTTPS enforcement middleware
export const enforceHTTPS = (req: Request, res: Response, next: NextFunction) => {
  if (process.env.NODE_ENV === 'production' && req.header('x-forwarded-proto') !== 'https') {
    return res.redirect(`https://${req.header('host')}${req.url}`);
  }
  next();
};

// Security logging middleware
export const securityLogger = (req: Request, res: Response, next: NextFunction) => {
  // Log sensitive operations
  if (req.path.includes('/auth/') || req.path.includes('/admin/')) {
    console.log(`[SECURITY] ${req.method} ${req.path} from ${req.ip} - ${req.get('User-Agent')}`);
  }
  next();
};

server/utils/errorHandler.ts.content:
import { Request, Response, NextFunction } from 'express';

export interface ApiError extends Error {
  statusCode?: number;
  code?: string;
}

export class AppError extends Error implements ApiError {
  public statusCode: number;
  public code: string;
  public isOperational: boolean;

  constructor(message: string, statusCode: number = 500, code: string = 'INTERNAL_ERROR') {
    super(message);
    this.statusCode = statusCode;
    this.code = code;
    this.isOperational = true;

    Error.captureStackTrace(this, this.constructor);
  }
}

// Standard error response format
export const formatErrorResponse = (error: ApiError, includeStack: boolean = false) => {
  const response: any = {
    success: false,
    error: {
      message: error.message || 'An unexpected error occurred',
      code: error.code || 'INTERNAL_ERROR',
      timestamp: new Date().toISOString()
    }
  };

  if (includeStack && process.env.NODE_ENV === 'development') {
    response.error.stack = error.stack;
  }

  return response;
};

// Centralized error handling middleware
export const errorHandler = (
  error: ApiError,
  req: Request,
  res: Response,
  next: NextFunction
) => {
  let { statusCode = 500, message, code } = error;

  // Don't log sensitive information in production
  if (process.env.NODE_ENV === 'production') {
    // Filter out sensitive errors
    if (statusCode === 500) {
      message = 'Internal server error';
      code = 'INTERNAL_ERROR';
    }
  } else {
    // Log full error details in development
    console.error('Error details:', {
      message: error.message,
      stack: error.stack,
      url: req.url,
      method: req.method,
      ip: req.ip,
      userAgent: req.get('User-Agent')
    });
  }

  // Send error response
  res.status(statusCode).json(formatErrorResponse({
    message,
    code,
    statusCode
  } as ApiError, process.env.NODE_ENV === 'development'));
};

// Async wrapper for route handlers
export const asyncHandler = (fn: Function) => (req: Request, res: Response, next: NextFunction) => {
  Promise.resolve(fn(req, res, next)).catch(next);
};

// Common error generators
export const createValidationError = (message: string) => 
  new AppError(message, 400, 'VALIDATION_ERROR');

export const createAuthError = (message: string = 'Authentication required') => 
  new AppError(message, 401, 'AUTH_ERROR');

export const createForbiddenError = (message: string = 'Access forbidden') => 
  new AppError(message, 403, 'FORBIDDEN_ERROR');

export const createNotFoundError = (resource: string = 'Resource') => 
  new AppError(`${resource} not found`, 404, 'NOT_FOUND');

export const createConflictError = (message: string) => 
  new AppError(message, 409, 'CONFLICT_ERROR');

export const createRateLimitError = () => 
  new AppError('Too many requests', 429, 'RATE_LIMIT_ERROR');

export const createServerError = (message: string = 'Internal server error') => 
  new AppError(message, 500, 'SERVER_ERROR');

client/src/components/AlarmListPanel.tsx.content:
import React, { useState } from 'react';
import { useAlarms } from '@/hooks/useAlarm';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Loader2, Bell, Trash2, Plus, Clock } from 'lucide-react';
import { format, addMinutes, addHours, addDays } from 'date-fns';

export default function AlarmListPanel() {
  const {
    alarms,
    isLoading,
    createAlarm,
    deleteAlarm,
    isCreating,
    isDeleting,
    requestNotificationPermission
  } = useAlarms();

  const [showCreateForm, setShowCreateForm] = useState(false);
  const [alarmLabel, setAlarmLabel] = useState('Wellness Check-in');
  const [timeOption, setTimeOption] = useState('15min');
  const [customMinutes, setCustomMinutes] = useState(30);

  const getAlarmTime = () => {
    const now = new Date();
    switch (timeOption) {
      case '15min': return addMinutes(now, 15);
      case '30min': return addMinutes(now, 30);
      case '1hour': return addHours(now, 1);
      case '2hours': return addHours(now, 2);
      case '4hours': return addHours(now, 4);
      case '1day': return addDays(now, 1);
      case 'custom': return addMinutes(now, customMinutes);
      default: return addMinutes(now, 30);
    }
  };

  const handleCreateAlarm = async () => {
    const hasPermission = await requestNotificationPermission();
    if (!hasPermission) {
      return;
    }

    const triggerAt = getAlarmTime();

    createAlarm({
      label: alarmLabel,
      triggerAt,
      isActive: true,
      isRecurring: false,
      notificationSent: false
    });

    // Reset form
    setAlarmLabel('Wellness Check-in');
    setTimeOption('15min');
    setShowCreateForm(false);
  };

  const formatAlarmTime = (triggerAt: string | Date) => {
    const date = new Date(triggerAt);
    const now = new Date();
    const isToday = date.toDateString() === now.toDateString();

    if (isToday) {
      return `Today at ${format(date, 'h:mm a')}`;
    } else {
      return format(date, 'MMM d, h:mm a');
    }
  };

  const getTimeUntilAlarm = (triggerAt: string | Date) => {
    const date = new Date(triggerAt);
    const now = new Date();
    const diffMs = date.getTime() - now.getTime();

    if (diffMs <= 0) return 'Past due';

    const diffMinutes = Math.floor(diffMs / (1000 * 60));
    const diffHours = Math.floor(diffMinutes / 60);
    const diffDays = Math.floor(diffHours / 24);

    if (diffDays > 0) return `in ${diffDays} day${diffDays > 1 ? 's' : ''}`;
    if (diffHours > 0) return `in ${diffHours} hour${diffHours > 1 ? 's' : ''}`;
    return `in ${diffMinutes} minute${diffMinutes > 1 ? 's' : ''}`;
  };

  if (isLoading) {
    return (
      <Card className="bg-gradient-to-br from-blue-50 to-indigo-50 dark:from-blue-950 dark:to-indigo-950 border-blue-200 dark:border-blue-800">
        <CardContent className="flex items-center justify-center py-8">
          <Loader2 className="h-6 w-6 animate-spin text-blue-600" />
          <span className="ml-2 text-blue-600">Loading alarms...</span>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="bg-gradient-to-br from-blue-50 to-indigo-50 dark:from-blue-950 dark:to-indigo-950 border-blue-200 dark:border-blue-800">
      <CardHeader>
        <CardTitle className="flex items-center gap-2 text-blue-900 dark:text-blue-100">
          <Bell className="h-5 w-5" />
          Wellness Reminders
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        {/* Create Alarm Button */}
        {!showCreateForm && (
          <Button 
            onClick={() => setShowCreateForm(true)}
            className="w-full bg-blue-600 hover:bg-blue-700 text-white"
          >
            <Plus className="h-4 w-4 mr-2" />
            Schedule New Reminder
          </Button>
        )}

        {/* Create Alarm Form */}
        {showCreateForm && (
          <div className="space-y-4 p-4 bg-white dark:bg-blue-900 rounded-lg border border-blue-200 dark:border-blue-700">
            <div>
              <Label htmlFor="alarm-label" className="text-blue-900 dark:text-blue-100">
                Reminder Message
              </Label>
              <Input
                id="alarm-label"
                value={alarmLabel}
                onChange={(e: React.ChangeEvent<HTMLInputElement>) => setAlarmLabel(e.target.value)}
                placeholder="Enter reminder message"
                className="mt-1"
              />
            </div>

            <div>
              <Label htmlFor="time-option" className="text-blue-900 dark:text-blue-100">
                When to remind you
              </Label>
              <Select value={timeOption} onValueChange={setTimeOption}>
                <SelectTrigger className="mt-1">
                  <SelectValue placeholder="Select time" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="15min">In 15 minutes</SelectItem>
                  <SelectItem value="30min">In 30 minutes</SelectItem>
                  <SelectItem value="1hour">In 1 hour</SelectItem>
                  <SelectItem value="2hours">In 2 hours</SelectItem>
                  <SelectItem value="4hours">In 4 hours</SelectItem>
                  <SelectItem value="1day">Tomorrow</SelectItem>
                  <SelectItem value="custom">Custom</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {timeOption === 'custom' && (
              <div>
                <Label htmlFor="custom-minutes" className="text-blue-900 dark:text-blue-100">
                  Minutes from now
                </Label>
                <Input
                  id="custom-minutes"
                  type="number"
                  min="1"
                  max="10080" // 1 week
                  value={customMinutes}
                  onChange={(e: React.ChangeEvent<HTMLInputElement>) => setCustomMinutes(parseInt(e.target.value) || 30)}
                  className="mt-1"
                />
              </div>
            )}

            <div className="flex gap-2">
              <Button 
                onClick={handleCreateAlarm}
                disabled={isCreating}
                className="flex-1 bg-blue-600 hover:bg-blue-700 text-white"
              >
                {isCreating ? (
                  <>
                    <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                    Scheduling...
                  </>
                ) : (
                  <>
                    <Bell className="h-4 w-4 mr-2" />
                    Schedule
                  </>
                )}
              </Button>
              <Button 
                variant="outline" 
                onClick={() => setShowCreateForm(false)}
                className="border-blue-200 text-blue-600 hover:bg-blue-50"
              >
                Cancel
              </Button>
            </div>
          </div>
        )}

        {/* Alarm List */}
        {alarms.length === 0 ? (
          <div className="text-center py-8">
            <Clock className="h-12 w-12 mx-auto text-blue-300 dark:text-blue-600 mb-3" />
            <p className="text-blue-600 dark:text-blue-300">No reminders scheduled</p>
            <p className="text-sm text-blue-500 dark:text-blue-400">
              Schedule a wellness reminder to stay on track with your mental health goals
            </p>
          </div>
        ) : (
          <div className="space-y-3">
            <h4 className="font-medium text-blue-900 dark:text-blue-100">
              Scheduled Reminders ({alarms.length})
            </h4>
            {alarms.map((alarm) => (
              <div
                key={alarm.id}
                className="flex items-center justify-between p-3 bg-white dark:bg-blue-900 rounded-lg border border-blue-200 dark:border-blue-700"
              >
                <div className="flex-1">
                  <p className="font-medium text-blue-900 dark:text-blue-100">
                    {alarm.label}
                  </p>
                  <p className="text-sm text-blue-600 dark:text-blue-300">
                    {formatAlarmTime(alarm.triggerAt)}
                  </p>
                  <p className="text-xs text-blue-500 dark:text-blue-400">
                    {getTimeUntilAlarm(alarm.triggerAt)}
                  </p>
                </div>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => deleteAlarm(alarm.id)}
                  disabled={isDeleting}
                  className="text-red-600 hover:text-red-700 hover:bg-red-50 dark:hover:bg-red-900/20"
                >
                  <Trash2 className="h-4 w-4" />
                </Button>
              </div>
            ))}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

