                {!collapsedSections.mirrors && (
                  <div className="space-y-1">
                    {[
                      { id: 'questions', label: 'Get to Know Me' },
                      { id: 'journal', label: 'Journal' },
                      { id: 'daily', label: 'Reflection' },
