import React, { useState, useCallback } from 'react';

export interface Toast {
  id: string;
  title: string;
  description?: string;
  variant?: 'default' | 'destructive' | 'success';
  duration?: number;
}

interface ToastState {
  toasts: Toast[];
}

const toastState: ToastState = {
  toasts: [],
};

const listeners: Set<(state: ToastState) => void> = new Set();

function emitChange() {
  listeners.forEach((listener) => listener(toastState));
}

let toastCount = 0;

function genId() {
  toastCount = (toastCount + 1) % Number.MAX_VALUE;
  return toastCount.toString();
}

function addToast(toast: Omit<Toast, 'id'>) {
  const id = genId();
  const newToast: Toast = {
    ...toast,
    id,
    duration: toast.duration ?? 5000,
  };

  toastState.toasts = [...toastState.toasts, newToast];
  emitChange();

  // Auto remove after duration
  if (newToast.duration && newToast.duration > 0) {
    setTimeout(() => {
      removeToast(id);
    }, newToast.duration);
  }

  return id;
}

function removeToast(id: string) {
  toastState.toasts = toastState.toasts.filter((toast) => toast.id !== id);
  emitChange();
}

export function useToast() {
  const [state, setState] = useState<ToastState>(toastState);

  React.useEffect(() => {
    listeners.add(setState);
    return () => {
      listeners.delete(setState);
    };
  }, []);

  const toast = useCallback((props: Omit<Toast, 'id'>) => {
    return addToast(props);
  }, []);

  const dismiss = useCallback((toastId: string) => {
    removeToast(toastId);
  }, []);

  return {
    toast,
    dismiss,
    toasts: state.toasts,
  };
}

client/src/hooks/useAlarm.ts.content:
import { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { useToast } from '@/hooks/use-toast';
import type { Alarm, InsertAlarm } from '@shared/schema';

interface AlarmResponse {
  alarms: Alarm[];
}

interface CreateAlarmResponse {
  success: boolean;
  alarm: Alarm;
  message: string;
}

interface DeleteAlarmResponse {
  success: boolean;
  message: string;
}

export function useAlarms() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch user's alarms
  const alarmsQuery = useQuery({
    queryKey: ['/api/alarms'],
    queryFn: async (): Promise<AlarmResponse> => {
      const response = await fetch('/api/alarms', {
        headers: {
          'X-Device-Fingerprint': localStorage.getItem('deviceFingerprint') || '',
          'X-Session-ID': localStorage.getItem('sessionId') || ''
        }
      });

      if (!response.ok) {
        throw new Error('Failed to fetch alarms');
      }

      return response.json();
    }
  });

  // Create new alarm
  const createAlarmMutation = useMutation({
    mutationFn: async (data: Omit<InsertAlarm, 'userId'> & { triggerAt: Date | string }): Promise<CreateAlarmResponse> => {
      const response = await fetch('/api/alarms', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-Device-Fingerprint': localStorage.getItem('deviceFingerprint') || '',
          'X-Session-ID': localStorage.getItem('sessionId') || ''
        },
        body: JSON.stringify({
          ...data,
          triggerAt: typeof data.triggerAt === 'string' ? data.triggerAt : data.triggerAt.toISOString()
        })
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || 'Failed to create alarm');
      }

      return response.json();
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ['/api/alarms'] });
      toast({
        title: ' Alarm Scheduled',
        description: data.message
      });
    },
    onError: (error) => {
      toast({
        title: ' Failed to Schedule Alarm',
        description: error.message,
        variant: 'destructive'
      });
    }
  });

  // Delete alarm
  const deleteAlarmMutation = useMutation({
    mutationFn: async (id: number): Promise<DeleteAlarmResponse> => {
      const response = await fetch('/api/alarms', {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'X-Device-Fingerprint': localStorage.getItem('deviceFingerprint') || '',
          'X-Session-ID': localStorage.getItem('sessionId') || ''
        },
        body: JSON.stringify({ id })
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || 'Failed to delete alarm');
      }

      return response.json();
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ['/api/alarms'] });
      toast({
        title: ' Alarm Deleted',
        description: data.message
      });
    },
    onError: (error) => {
      toast({
        title: ' Failed to Delete Alarm',
        description: error.message,
        variant: 'destructive'
      });
    }
  });

  // Request notification permission
  const requestNotificationPermission = async (): Promise<boolean> => {
    if (!('Notification' in window)) {
      toast({
        title: ' Not Supported',
        description: 'This browser does not support notifications',
        variant: 'destructive'
      });
      return false;
    }

    if (Notification.permission === 'granted') {
      return true;
    }

    if (Notification.permission === 'default') {
      const permission = await Notification.requestPermission();
      return permission === 'granted';
    }

    return false;
  };

  // Schedule browser notification for upcoming alarms
  const scheduleNotifications = () => {
    if (!alarmsQuery.data?.alarms) return;

    alarmsQuery.data.alarms.forEach((alarm) => {
      const now = new Date();
      const triggerTime = new Date(alarm.triggerAt);
      const timeUntilTrigger = triggerTime.getTime() - now.getTime();

      // Only schedule notifications for alarms within the next 24 hours
      if (timeUntilTrigger > 0 && timeUntilTrigger <= 24 * 60 * 60 * 1000) {
        setTimeout(() => {
          if (Notification.permission === 'granted') {
            new Notification(' Chakrai Wellness Reminder', {
              body: alarm.label || 'Time for your wellness check-in',
              icon: '/icon.png',
              tag: `chakrai-alarm-${alarm.id}`,
              requireInteraction: true
            });
          }
        }, timeUntilTrigger);
      }
    });
  };

  // Initialize notifications when alarms are loaded
  useEffect(() => {
    if (alarmsQuery.data?.alarms && Notification.permission === 'granted') {
      scheduleNotifications();
    }
  }, [alarmsQuery.data]);

  return {
    alarms: alarmsQuery.data?.alarms || [],
    isLoading: alarmsQuery.isLoading,
    error: alarmsQuery.error,
    createAlarm: createAlarmMutation.mutate,
    deleteAlarm: deleteAlarmMutation.mutate,
    isCreating: createAlarmMutation.isPending,
    isDeleting: deleteAlarmMutation.isPending,
    requestNotificationPermission,
    refetch: alarmsQuery.refetch
  };
}

client/src/hooks/useNeonCursorTrail.tsx.content:
import { useState, useEffect, useCallback, useRef } from 'react';

interface Point {
  x: number;
  y: number;
}

// Configuration for the trail
const TRAIL_LENGTH = 20; // Slightly longer trail
const UPDATE_INTERVAL = 16; // ~60fps update throttle

/**
 * Custom hook to track the mouse position and generate a series of points
 * representing the cursor's recent path, suitable for creating a trail effect.
 *
 * Uses requestAnimationFrame for smooth, throttled updates and maintains
 * a fixed-length array for trail points.
 */
export const useNeonCursorTrail = (): Point[] => {
  const [points, setPoints] = useState<Point[]>([]);
  const lastUpdateTime = useRef(0);
  const animationFrameId = useRef<number | null>(null);
  const lastPosition = useRef<Point>({ x: 0, y: 0 }); // Store last known position

  // Mouse move handler - captures the latest position
  const handleMouseMove = useCallback((event: MouseEvent) => {
    lastPosition.current = { x: event.clientX, y: event.clientY };
  }, []);

  // Animation loop function
  const updateTrail = useCallback(() => {
    const now = performance.now();
    const timeDelta = now - lastUpdateTime.current;

    // Update only if enough time has passed or if it's the first frame
    if (timeDelta >= UPDATE_INTERVAL || lastUpdateTime.current === 0) {
      setPoints(prevPoints => {
        const newPoints = [...prevPoints, { x: lastPosition.current.x, y: lastPosition.current.y }];
        // Keep only the last TRAIL_LENGTH points
        return newPoints.slice(Math.max(newPoints.length - TRAIL_LENGTH, 0));
      });
      lastUpdateTime.current = now;
    }

    // Request the next frame
    animationFrameId.current = requestAnimationFrame(updateTrail);
  }, []); // Dependencies: updateTrail depends on itself implicitly via requestAnimationFrame

  useEffect(() => {
    window.addEventListener('mousemove', handleMouseMove, { passive: true }); // Use passive listener for performance

    // Start the animation loop
    animationFrameId.current = requestAnimationFrame(updateTrail);

    // Cleanup function
    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      if (animationFrameId.current !== null) {
        cancelAnimationFrame(animationFrameId.current);
        animationFrameId.current = null;
      }
    };
  }, [handleMouseMove, updateTrail]); // Add dependencies

  return points;
};

