  }
});

// Legacy endpoint
router.post('/mood', async (req, res) => {
  req.url = '/';
  return router.handle(req, res);
});

// Generic entries endpoint for backward compatibility  
router.get('/entries', async (req, res) => {
  try {
    const { UserSessionManager } = await import('../userSession.js');
