import express from "express";
import { createServer } from "http";
import path from "path";
import { fileURLToPath } from 'url';
import cors from 'cors';
import { setupVite, serveStatic, log } from "./vite.js";
import routes from './routes.js';
import { 
  helmetConfig, 
  generalLimiter, 
  corsConfig, 
  enforceHTTPS, 
  securityLogger 
} from './middleware/security.js';
import { errorHandler } from './utils/errorHandler.js';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

const app = express();
const server = createServer(app);
const PORT = parseInt(process.env.PORT || '5000', 10);

// Trust proxy for rate limiting (must be before rate limiters)
// Configure trust proxy specifically for Replit environment
app.set('trust proxy', 1); // Trust first proxy only

// Performance monitoring middleware (applied early)
import { 
  requestTimer, 
  memoryMonitor, 
  compressionOptimizer,
  dbConnectionMonitor,
  cacheOptimizer 
} from './middleware/performanceMiddleware.js';

// Temporarily disable performance middleware causing slow loading issues
// app.use(requestTimer);
// app.use(memoryMonitor);
// app.use(compressionOptimizer);
// app.use(dbConnectionMonitor);
// app.use(cacheOptimizer);

// Security middleware (applied early)
app.use(enforceHTTPS);
app.use(helmetConfig);
app.use(securityLogger);

// Rate limiting - temporarily disabled to fix loading issues
// app.use(generalLimiter);

// CORS configuration (secure)
app.use(cors(corsConfig));

// Body parsing with limits suitable for audio responses
app.use(express.json({ limit: '50mb' })); // Restored original limit for audio functionality
app.use(express.urlencoded({ extended: true, limit: '50mb' }));

// CRITICAL: Priority API endpoints MUST come before ANY other middleware to prevent Vite interception

// Import storage for database operations
import { storage } from './storage.js';
import bcrypt from 'bcrypt';
import jwt from 'jsonwebtoken';
import crypto from 'crypto';

// JWT secret for authentication - MUST be set in production
const JWT_SECRET = process.env.JWT_SECRET;
if (!JWT_SECRET) {
  console.error('FATAL: JWT_SECRET environment variable is required');
  process.exit(1);
}

// Middleware for authentication
const authenticateToken = (req: any, res: any, next: any) => {
  const authHeader = req.headers['authorization'];
  const token = authHeader && authHeader.split(' ')[1];

  if (!token) {
    return res.status(401).json({ error: 'Access token required' });
  }

  jwt.verify(token, JWT_SECRET, (err: any, user: any) => {
    if (err) {
      return res.status(403).json({ error: 'Invalid or expired token' });
    }
    req.user = user;
    next();
  });
};

// Import security middleware for authentication endpoints
import { 
  authLimiter, 
  validateRegistration, 
  validateLogin, 
  handleValidationErrors 
} from './middleware/security.js';
import { asyncHandler, createConflictError, createAuthError } from './utils/errorHandler.js';

// Authentication endpoints with security middleware
app.post('/api/auth/register', 
  authLimiter,
  validateRegistration,
  handleValidationErrors,
  asyncHandler(async (req: any, res: any) => {
    const { email, password, name } = req.body;

    // Check if user already exists
    const existingUser = await storage.getUserByEmail(email);
    if (existingUser) {
      throw createConflictError('User already exists with this email');
    }

    // Hash password with higher salt rounds for better security
    const saltRounds = 14;
    const passwordHash = await bcrypt.hash(password, saltRounds);

    // Create user
    const user = await storage.createRegisteredUser({
      email,
      passwordHash,
      displayName: name,
      username: email.split('@')[0] + '_' + Date.now(),
      isAnonymous: false
    });

    // Generate JWT token with shorter expiry for better security
    const token = jwt.sign(
      { userId: user.id, email: user.email },
      JWT_SECRET,
      { expiresIn: '7d' } // Reduced from 30d for better security
    );

    // Store auth token
    await storage.createAuthToken({
      userId: user.id,
      token: token,
      expiresAt: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000), // 7 days
      deviceInfo: req.headers['user-agent'] || 'Unknown device'
    });

    res.json({
      success: true,
      user: {
        id: user.id,
        email: user.email,
        displayName: user.displayName,
        isAnonymous: false
      },
      token
    });
  })
);

app.post('/api/auth/login',
  authLimiter,
  validateLogin,
  handleValidationErrors,
  asyncHandler(async (req: any, res: any) => {
    const { email, password } = req.body;

    // Get user by email
    const user = await storage.getUserByEmail(email);
    if (!user || !user.passwordHash) {
      throw createAuthError('Invalid email or password');
    }

    // Verify password
    const isValidPassword = await bcrypt.compare(password, user.passwordHash);
    if (!isValidPassword) {
      throw createAuthError('Invalid email or password');
    }

    // Generate JWT token with shorter expiry
    const token = jwt.sign(
      { userId: user.id, email: user.email },
      JWT_SECRET,
      { expiresIn: '7d' }
    );

    // Store auth token
    await storage.createAuthToken({
      userId: user.id,
      token: token,
      expiresAt: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000), // 7 days
      deviceInfo: req.headers['user-agent'] || 'Unknown device'
    });

    res.json({
      success: true,
      user: {
        id: user.id,
        email: user.email,
        displayName: user.displayName,
        isAnonymous: user.isAnonymous
      },
      token
    });
  })
);

app.post('/api/auth/logout', authenticateToken, async (req: any, res) => {
  try {
    const token = req.headers['authorization']?.split(' ')[1];
    if (token) {
      await storage.deleteAuthToken(token);
    }
    res.json({ message: 'Logged out successfully' });
  } catch (error) {
    console.error('Logout error:', error);
    res.status(500).json({ error: 'Logout failed' });
  }
});

app.get('/api/auth/verify', authenticateToken, async (req: any, res) => {
  try {
    const user = await storage.getUserById(req.user.userId);
    if (!user) {
      return res.status(404).json({ error: 'User not found' });
    }

    res.json({
      user: {
        id: user.id,
        email: user.email,
        displayName: user.displayName,
        isAnonymous: user.isAnonymous
      }
    });
  } catch (error) {
    console.error('Token verification error:', error);
    res.status(500).json({ error: 'Token verification failed' });
  }
});

// Migration endpoint to convert anonymous user to registered user
app.post('/api/auth/migrate', async (req, res) => {
  try {
    const { anonymousUserId, email, password, name } = req.body;

    // Check if registered user already exists
    const existingUser = await storage.getUserByEmail(email);
    if (existingUser) {
      return res.status(400).json({ error: 'User already exists with this email' });
    }

    // Get anonymous user
    const anonymousUser = await storage.getUserById(anonymousUserId);
    if (!anonymousUser || !anonymousUser.isAnonymous) {
      return res.status(400).json({ error: 'Invalid anonymous user' });
    }

    // Hash password
    const saltRounds = 12;
    const passwordHash = await bcrypt.hash(password, saltRounds);

    // Convert anonymous user to registered user
    const updatedUser = await storage.migrateAnonymousToRegistered(anonymousUserId, {
      email,
      passwordHash,
      displayName: name,
      username: email.split('@')[0] + '_' + Date.now()
    });

    // Generate JWT token
    const token = jwt.sign(
      { userId: updatedUser.id, email: updatedUser.email },
      JWT_SECRET,
      { expiresIn: '30d' }
    );

    // Store auth token
    await storage.createAuthToken({
      userId: updatedUser.id,
      token: token,
      expiresAt: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000), // 30 days
      deviceInfo: req.headers['user-agent'] || 'Unknown device'
    });

    res.json({
      user: {
        id: updatedUser.id,
        email: updatedUser.email,
        displayName: updatedUser.displayName,
        isAnonymous: false
      },
      token
    });
  } catch (error) {
    console.error('Migration error:', error);
    res.status(500).json({ error: 'Migration failed' });
  }
});

