        quizCompleted: quizCompleted || false
      });
    } else {
      // Create new profile
      await storage.createUserProfile({
        userId,
        ...profileData,
        quizCompleted: quizCompleted || false
      });
    }

    res.json({ success: true });
  } catch (error) {
    console.error('Save user profile error:', error);
    res.status(500).json({ error: 'Failed to save user profile' });
  }
});

