        max_tokens: 150,
        temperature: 0.7
      });
      insights = aiResponse.choices[0].message.content || insights;
    } catch (error) {
      console.log('AI insights generation failed, using fallback');
    }

    const dashboard = {
      dashboard: {
        overview: {
          currentWellnessScore,
