          therapeuticEngagement: Math.min(100, (totalJournalEntries + totalMoodEntries) * 2),
          totalJournalEntries,
          totalMoodEntries,
          averageMood: Math.round(averageMood * 10) / 10
        },
        charts: {
          moodTrend,
          wellnessTrend: progressTracking.map(p => ({
            date: p.period,
            value: p.engagement,
            type: 'engagement'
