/**
 * Enterprise-Grade User Identity Management System
 * Ensures consistent, secure user identification for healthcare applications
 */

import crypto from 'crypto';
import { db } from '../db';
import { users } from '../../shared/schema';
import { eq } from 'drizzle-orm';

interface SecureUserSession {
  userId: number;
  sessionToken: string;
  deviceFingerprint: string;
  ipAddress: string;
  userAgent: string;
  createdAt: Date;
  lastAccess: Date;
  isValid: boolean;
}

interface UserIdentityResult {
  userId: number;
  sessionToken: string;
  isNewUser: boolean;
  securityLevel: 'HIGH' | 'MEDIUM' | 'LOW';
}

export class UserIdentityManager {
  private static readonly SESSION_EXPIRY = 24 * 60 * 60 * 1000; // 24 hours
  private static readonly MAX_SESSIONS_PER_USER = 5;

  /**
   * Securely identify or create user with comprehensive validation
   */
  static async identifyUser(
    deviceFingerprint: string,
    ipAddress: string,
    userAgent: string,
    sessionId?: string
  ): Promise<UserIdentityResult> {
    // Validate inputs
    if (!deviceFingerprint || !ipAddress || !userAgent) {
      throw new Error('Missing required security parameters');
    }

    // Check if user exists with this device fingerprint
    const existingUser = await db
      .select()
      .from(users)
      .where(eq(users.device_fingerprint, deviceFingerprint))
      .limit(1);

    if (existingUser.length > 0) {
      const user = existingUser[0];

      // Validate session integrity
      const sessionToken = this.generateSecureSessionToken(user.id, deviceFingerprint);

      // Update last access
      await db
        .update(users)
        .set({ 
          last_login: new Date(),
          session_token: sessionToken
        })
        .where(eq(users.id, user.id));

      return {
        userId: user.id,
        sessionToken,
        isNewUser: false,
        securityLevel: this.assessSecurityLevel(deviceFingerprint, ipAddress, userAgent)
      };
    }

    // Create new user with secure defaults
    const sessionToken = this.generateSecureSessionToken(0, deviceFingerprint);
    const secureUsername = this.generateSecureUsername();

    const newUser = await db
      .insert(users)
      .values({
        username: secureUsername,
        device_fingerprint: deviceFingerprint,
        session_token: sessionToken,
        ip_address: ipAddress,
        user_agent: userAgent,
        created_at: new Date(),
        last_login: new Date(),
        security_level: 'HIGH',
        is_active: true
      })
      .returning();

    if (!newUser.length) {
      throw new Error('Failed to create secure user session');
    }

    return {
      userId: newUser[0].id,
      sessionToken,
      isNewUser: true,
      securityLevel: 'HIGH'
    };
  }

  /**
   * Validate existing session
   */
  static async validateSession(
    userId: number,
    sessionToken: string,
    deviceFingerprint: string
  ): Promise<boolean> {
    const user = await db
      .select()
      .from(users)
      .where(eq(users.id, userId))
      .limit(1);

    if (!user.length) return false;

    const userData = user[0];

    // Validate session token
    const expectedToken = this.generateSecureSessionToken(userId, deviceFingerprint);
    if (userData.session_token !== expectedToken) return false;

    // Check device fingerprint consistency
    if (userData.device_fingerprint !== deviceFingerprint) return false;

    // Check session expiry
    const lastLogin = new Date(userData.last_login || userData.created_at);
    const sessionAge = Date.now() - lastLogin.getTime();
    if (sessionAge > this.SESSION_EXPIRY) return false;

    return true;
  }

  /**
   * Generate cryptographically secure session token
   */
  private static generateSecureSessionToken(userId: number, deviceFingerprint: string): string {
    const timestamp = Date.now();
    const randomBytes = crypto.randomBytes(32).toString('hex');
    const payload = `${userId}:${deviceFingerprint}:${timestamp}:${randomBytes}`;

    return crypto
      .createHmac('sha256', process.env.SESSION_SECRET || 'default-secret-change-in-production')
      .update(payload)
      .digest('hex');
  }

  /**
   * Generate secure username
   */
  private static generateSecureUsername(): string {
    const timestamp = Date.now().toString(36);
    const random = crypto.randomBytes(8).toString('hex');
    return `user_${timestamp}_${random}`;
  }

  /**
   * Assess security level based on request parameters
   */
  private static assessSecurityLevel(
    deviceFingerprint: string,
    ipAddress: string,
    userAgent: string
  ): 'HIGH' | 'MEDIUM' | 'LOW' {
    let score = 0;

    // Check device fingerprint complexity
    if (deviceFingerprint.length > 20) score += 2;

    // Check if IP is private/local (development)
    if (ipAddress.startsWith('127.') || ipAddress.startsWith('192.168.') || ipAddress.startsWith('10.')) {
      score += 1;
    } else {
      score += 3; // Public IP gets higher security
    }

    // Check user agent complexity
    if (userAgent.length > 50) score += 2;

    if (score >= 6) return 'HIGH';
    if (score >= 4) return 'MEDIUM';
    return 'LOW';
  }

  /**
   * Audit user access for compliance
   */
  static async auditUserAccess(
    userId: number,
    action: string,
    ipAddress: string,
    userAgent: string
  ): Promise<void> {
    // Implementation would log to audit table for healthcare compliance
    console.log(`[AUDIT] User ${userId} performed ${action} from ${ipAddress}`);
  }
}

